// Televisions.jsx
import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import "./Category.css";
import { Link } from "react-router-dom";

const Televisions = () => {
  const allTelevisions = [
    {
      id: 10,
      img: "/img/tovar/oled_tv.jpg",
      model: "OLED55C3",
      brand: "LG",
      oldPrice: 2000,
      newPrice: 1800,
      availability: "В наличии",
      memory: "N/A",
      specs: {
        screen: "55'' OLED",
        cores: "N/A",
        power: "120W",
        ram: "N/A",
        storage: "N/A",
        camera: "N/A"
      }
    },
    {
      id: 11,
      img: "/img/tovar/qled_tv.jpg",
      model: "QLED Q80C",
      brand: "Samsung",
      oldPrice: 1900,
      newPrice: 1700,
      availability: "В наличии",
      memory: "N/A",
      specs: {
        screen: "65'' QLED",
        cores: "N/A",
        power: "150W",
        ram: "N/A",
        storage: "N/A",
        camera: "N/A"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    availability: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    setFilters((prev) => ({ ...prev, [filterName]: value }));
  };

  const filteredTelevisions = allTelevisions.filter((tv) => {
    const price = tv.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;

    return (
      (!filters.brand || tv.brand === filters.brand) &&
      (!filters.availability || tv.availability === filters.availability) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="category-catalog">
        <h1>Каталог телевизоров</h1>
        <div className="product-grid">
          {filteredTelevisions.map((tv) => (
            <Link
              to={`/products/${tv.id}`}
              key={tv.id}
              className="product-link"
            >
              <ProductCard
                {...tv}
                discount={
                  tv.discount
                    ? `${Math.round((1 - tv.newPrice / tv.oldPrice) * 100)}%`
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Televisions;
