import React, { useEffect, useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import '../CategoryPage/CategoryPage';

const Smartphones = () => {
  const [smartphones, setSmartphones] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    ram: '',
    storage: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (name, value) => {
    if (name === 'reset') {
      setFilters({ brand: '', ram: '', storage: '', minPrice: '', maxPrice: '' });
    } else {
      setFilters((prev) => ({ ...prev, [name]: value }));
    }
  };

  // üì° –§–æ—Ä–º–∏—Ä–æ–≤–∞–Ω–∏–µ URL —Å query-–ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏
  const buildQueryParams = () => {
    const params = new URLSearchParams();
    if (filters.brand) params.append('brand', filters.brand);
    if (filters.ram) params.append('ram', filters.ram);
    if (filters.storage) params.append('storage', filters.storage);
    if (filters.minPrice) params.append('minPrice', filters.minPrice);
    if (filters.maxPrice) params.append('maxPrice', filters.maxPrice);
    return params.toString();
  };

  // üì• –ü–æ–ª—É—á–µ–Ω–∏–µ —Å–º–∞—Ä—Ç—Ñ–æ–Ω–æ–≤ —Å API
  const fetchSmartphones = async () => {
    try {
      const query = buildQueryParams();
      const response = await fetch(`http://localhost:5085/api/products/category/–°–º–∞—Ä—Ç—Ñ–æ–Ω—ã?${query}`);
      const data = await response.json();
      setSmartphones(data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å–º–∞—Ä—Ç—Ñ–æ–Ω–æ–≤:", error);
    }
  };

  // ‚õì –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  useEffect(() => {
    fetchSmartphones();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="–°–º–∞—Ä—Ç—Ñ–æ–Ω—ã" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>–ö–∞—Ç–∞–ª–æ–≥ —Å–º–∞—Ä—Ç—Ñ–æ–Ω–æ–≤</h1>
        <div className="product-grid">
          {smartphones.map(phone => (
            <ProductCard
              key={phone.id}
              id={phone.id}
              img={phone.img}
              model={phone.model}
              oldPrice={phone.oldPrice}
              newPrice={phone.newPrice}
              availability={phone.availability}
              discount={phone.oldPrice > phone.newPrice ? Math.round((1 - phone.newPrice / phone.oldPrice) * 100) : null}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
