// Tablets.jsx
import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import "./Category.css";
import { Link } from "react-router-dom";

const Tablets = () => {
  const allTablets = [
    {
      id: 6,
      img: "/img/tovar/ipad.jpg",
      model: "iPad Pro 11",
      brand: "Apple",
      oldPrice: 1200,
      newPrice: 1000,
      availability: "В наличии",
      memory: "256GB",
      specs: {
        screen: "11'' Liquid Retina",
        cores: "8-ядерный M2",
        power: "20W",
        ram: "8GB",
        storage: "256GB",
        camera: "12MP + 10MP"
      }
    },
    {
      id: 7,
      img: "/img/tovar/galaxy_tab.jpg",
      model: "Galaxy Tab S9",
      brand: "Samsung",
      oldPrice: 1100,
      newPrice: 900,
      availability: "В наличии",
      memory: "128GB",
      specs: {
        screen: "12.4'' Dynamic AMOLED",
        cores: "8-ядерный Snapdragon 8 Gen 2",
        power: "45W",
        ram: "12GB",
        storage: "128GB",
        camera: "13MP + 12MP"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    availability: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    setFilters((prev) => ({ ...prev, [filterName]: value }));
  };

  const filteredTablets = allTablets.filter((tablet) => {
    const price = tablet.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;

    return (
      (!filters.brand || tablet.brand === filters.brand) &&
      (!filters.availability || tablet.availability === filters.availability) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="category-catalog">
        <h1>Каталог планшетов</h1>
        <div className="product-grid">
          {filteredTablets.map((tablet) => (
            <Link
              to={`/products/${tablet.id}`}
              key={tablet.id}
              className="product-link"
            >
              <ProductCard
                {...tablet}
                discount={
                  tablet.discount
                    ? `${Math.round(
                        (1 - tablet.newPrice / tablet.oldPrice) * 100
                      )}%`
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Tablets;
