import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({
  id,
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  availability
}) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [isCompared, setIsCompared] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const compare = JSON.parse(localStorage.getItem("compare")) || [];
    setIsCompared(compare.includes(id));

    const user = JSON.parse(localStorage.getItem("user"));
    if (user?.userId) {
      fetch(`http://localhost:5085/api/favorite/${user.userId}`)
        .then(res => res.json())
        .then(data => {
          const isInFavorites = data.some(fav => fav.product.id === id);
          setIsFavorite(isInFavorites);
        })
        .catch(err => console.error("Ошибка получения избранного:", err));
    }
  }, [id]);

  const handleCardClick = () => {
    navigate(`/products/${id}`);
  };

  const handleAddToCart = async (e) => {
    e.stopPropagation();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) {
      alert("Вы не авторизованы");
      navigate('/login');
      return;
    }

    try {
      const response = await fetch("http://localhost:5085/api/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.userId,
          productId: id,
          quantity: 1
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Ошибка от сервера:", errorText);
        alert("Ошибка при добавлении в корзину");
        return;
      }

      alert("Товар добавлен в корзину!");
    } catch (error) {
      console.error("Ошибка при добавлении в корзину:", error);
      alert("Ошибка добавления");
    }
  };

  const handleCompare = (e) => {
    e.stopPropagation();
    const compare = JSON.parse(localStorage.getItem("compare")) || [];

    if (!compare.includes(id)) {
      compare.push(id);
      localStorage.setItem("compare", JSON.stringify(compare));
      setIsCompared(true);
      alert("Товар добавлен к сравнению");
    } else {
      const updated = compare.filter(p => p !== id);
      localStorage.setItem("compare", JSON.stringify(updated));
      setIsCompared(false);
      alert("Товар удалён из сравнения");
    }
  };

  const handleToggleFavorite = async (e) => {
    e.stopPropagation();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) {
      alert("Войдите в аккаунт");
      return;
    }

    try {
      const url = isFavorite
        ? `http://localhost:5085/api/favorite/${user.userId}/${id}`
        : "http://localhost:5085/api/favorite";
      const method = isFavorite ? "DELETE" : "POST";

      const body = isFavorite
        ? null
        : JSON.stringify({ userId: user.userId, productId: id });

      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body
      });

      if (res.ok) {
        setIsFavorite(!isFavorite);
        alert(isFavorite ? "Удалено из избранного" : "Добавлено в избранное");
      } else {
        const errorData = await res.text();
        console.error("Ошибка избранного:", errorData);
        alert("Ошибка при обновлении избранного");
      }
    } catch (err) {
      console.error("Ошибка избранного:", err);
      alert("Ошибка при обновлении избранного");
    }
  };

  return (
    <div className="product-card" onClick={handleCardClick} style={{ cursor: 'pointer' }}>
      <div className="product-image-wrapper">
        {discount > 0 && <div className="discount-badge">-{discount}%</div>}
        {availability === "В наличии" && (
          <div className="availability-badge in-stock">В наличии</div>
        )}
        <img src={img} alt={model} className="product-image" />
      </div>

      <div className="product-content">
        <h3 className="product-title">{model}</h3>

        <div className="price-wrapper">
          {oldPrice > newPrice && (
            <div className="old-price">{oldPrice.toLocaleString()} BYN</div>
          )}
          <div className="new-price">{newPrice.toLocaleString()} BYN</div>
        </div>

        <div className="product-footer">
          <button className="add-to-cart-btn" onClick={handleAddToCart}>В корзину</button>
          <button
            className="add-to-cart-btn"
            style={{ backgroundColor: isCompared ? "#28a745" : "#555" }}
            onClick={handleCompare}
          >
            {isCompared ? "В сравнении" : "Сравнить"}
          </button>
          <button
            className={`favorite-button ${isFavorite ? 'active' : ''}`}
            onClick={handleToggleFavorite}
            title={isFavorite ? "Удалить из избранного" : "Добавить в избранное"}
          >
            ❤️
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
