// Filters.jsx
import React from 'react';
import './Filters.css';

const Filters = ({ filters, onFilterChange }) => {
  const brands = ['Apple', 'Samsung', 'Xiaomi', 'Huawei', 'Google'];

  return (
    <div className="filters-panel">
      <h3 className="filters-title">Фильтровать товары</h3>

      <div className="filter-section">
        <label className="filter-label">Бренд</label>
        <div className="brand-grid">
          {brands.map(brand => (
            <button
              key={brand}
              className={`brand-pill ${filters.brand === brand ? 'active' : ''}`}
              onClick={() => onFilterChange('brand', filters.brand === brand ? '' : brand)}
            >
              {brand}
            </button>
          ))}
        </div>
      </div>

      <div className="filter-section">
        <label className="filter-label">Наличие</label>
        <div className="availability-toggles">
          <button
            className={`toggle-btn ${filters.availability === 'В наличии' ? 'active' : ''}`}
            onClick={() => onFilterChange('availability', filters.availability === 'В наличии' ? '' : 'В наличии')}
          >
            В наличии
          </button>
          <button
            className={`toggle-btn ${filters.availability === 'Нет в наличии' ? 'active' : ''}`}
            onClick={() => onFilterChange('availability', filters.availability === 'Нет в наличии' ? '' : 'Нет в наличии')}
          >
            Предзаказ
          </button>
        </div>
      </div>

      <div className="filter-section">
        <label className="filter-label">Цена, ₽</label>
        <div className="price-range">
          <input
            type="number"
            className="price-input"
            placeholder="От"
            value={filters.minPrice}
            onChange={(e) => onFilterChange('minPrice', Math.max(0, e.target.value))}
          />
          <span className="price-separator">—</span>
          <input
            type="number"
            className="price-input"
            placeholder="До"
            value={filters.maxPrice}
            onChange={(e) => onFilterChange('maxPrice', Math.max(0, e.target.value))}
          />
        </div>
      </div>

      <button
        className="reset-filters"
        onClick={() => onFilterChange('reset')}
      >
        Сбросить фильтры
      </button>
    </div>
  );
};

export default Filters;
