import React from 'react';
import './ProductCard.css';

// Компонент для отображения изображений
const ProductImages = ({ images }) => {
  // Если изображений больше двух, выбираем первые два
  const shownImages = images.slice(0, 2);
  return (
    <div className="product-images-container">
      {shownImages.map((src, index) => (
        <img
          key={index}
          src={src}
          alt={`Изображение товара ${index + 1}`}
          className="product-image"
        />
      ))}
    </div>
  );
};

// Компонент описания товара – модель и память
const ProductDescription = ({ model, memory }) => {
  return (
    <div className="product-description">
      <div className="product-model">{model}</div>
      <div className="product-memory">{memory}</div>
    </div>
  );
};

// Компонент отображения цены
const PriceDisplay = ({ price, originalPrice }) => {
  const hasDiscount = originalPrice && originalPrice > price;
  return (
    <div className="price-container">
      {hasDiscount ? (
        <div className="discount-price">
          {/* Текущая цена – слева, крупнее и чёрная */}
          <div className="current-price">{price}₽</div>
          {/* Оригинальная цена – справа, серой и зачёркнутая */}
          <div className="original-price">{originalPrice}₽</div>
        </div>
      ) : (
        <div className="regular-price">{price}₽</div>
      )}
    </div>
  );
};

// Компонент наличия товара
const Availability = ({ inStock }) => (
  <div className="product-availability">
    {inStock ? 'В наличии' : 'Нет в наличии'}
  </div>
);

// Компонент кнопки заказа
const OrderButton = () => (
  <button className="order-button">Заказать</button>
);

// Главный компонент карточки товара
const ProductCard = ({ product }) => {
  // product содержит: images, model, memory, price, originalPrice, inStock
  const { images, model, memory, price, originalPrice, inStock } = product;
  return (
    <div className="product-card">
      <div className="product-title">Популярное</div>
      <div className="product-card-inner">
        <div className="product-image-section">
          <ProductImages images={images} />
        </div>
        <ProductDescription model={model} memory={memory} />
        <PriceDisplay price={price} originalPrice={originalPrice} />
        <Availability inStock={inStock} />
        <OrderButton />
      </div>
    </div>
  );
};

export default ProductCard;
