import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./ComparePage.css";

const ComparePage = () => {
  const [products, setProducts] = useState([]);
  const [groupedSpecs, setGroupedSpecs] = useState({});
  const [productSpecs, setProductSpecs] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    let compareIds = JSON.parse(localStorage.getItem("compare")) || [];
    compareIds = compareIds.filter(id => id !== null && id !== "null");

    Promise.all(compareIds.map(async (id) => {
      try {
        const [productRes, specRes] = await Promise.all([
          fetch(`http://localhost:5085/api/products/${id}`),
          fetch(`http://localhost:5085/api/specifications/${id}`)
        ]);
        if (!productRes.ok || !specRes.ok) throw new Error("Ошибка загрузки");

        const product = await productRes.json();
        const spec = await specRes.json();
        return { product, spec };
      } catch (err) {
        console.error(`Ошибка загрузки данных для товара ${id}:`, err);
        return null;
      }
    }))
    .then(data => {
      const valid = data.filter(p => p && p.product);
      setProducts(valid.map(p => p.product));

      // все характеристики для карточек
      const specsByProduct = {};
      valid.forEach(({ product, spec }) => {
        specsByProduct[product.productId] = spec;
      });
      setProductSpecs(specsByProduct);

      // данные для таблицы сравнения
      const grouped = {};
      valid.forEach(({ product, spec }) => {
        spec.forEach(({ groupName, attributeName, attributeValue }) => {
          if (!grouped[groupName]) grouped[groupName] = {};
          if (!grouped[groupName][attributeName]) grouped[groupName][attributeName] = {};
          grouped[groupName][attributeName][product.productId] = attributeValue;
        });
      });
      setGroupedSpecs(grouped);
    });
  }, []);

  return (
    <div className="compare-page">
      <h2>Сравнение товаров</h2>

      {products.length === 0 ? (
        <p>Нет товаров для сравнения</p>
      ) : (
        <>
          <div className="compare-cards">
            {products.map((product) => (
              <div key={product.productId} className="compare-card">
                <img
                  src={product.imageUrl || "/default.jpg"}
                  alt={product.name || "Изображение"}
                  onError={(e) => (e.target.src = "/default.jpg")}
                />
                <h4>{product.name || "Без названия"}</h4>
                <p>{product.price ? `${product.price} BYN` : "Цена не указана"}</p>

                {/* Характеристики прямо в карточке */}
                {productSpecs[product.productId] && (
                  <ul className="spec-list">
                    {productSpecs[product.productId].map((s, i) => (
                      <li key={i}>
                        <strong>{s.groupName} — {s.attributeName}:</strong> {s.attributeValue}
                      </li>
                    ))}
                  </ul>
                )}

                <button
                  onClick={() => {
                    const updatedIds = products
                      .filter(p => p.productId !== product.productId)
                      .map(p => p.productId);
                    localStorage.setItem("compare", JSON.stringify(updatedIds));
                    window.location.reload();
                  }}
                >
                  Удалить
                </button>
              </div>
            ))}
          </div>

          <table className="compare-table">
            <thead>
              <tr>
                <th>Характеристика</th>
                {products.map(p => (
                  <th key={p.productId}>{p.name}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.entries(groupedSpecs).map(([groupName, attributes]) => (
                <React.Fragment key={groupName}>
                  <tr>
                    <th colSpan={products.length + 1} style={{ backgroundColor: "#ddd" }}>
                      {groupName}
                    </th>
                  </tr>
                  {Object.entries(attributes).map(([attrName, values]) => (
                    <tr key={attrName}>
                      <td>{attrName}</td>
                      {products.map(p => (
                        <td key={p.productId}>
                          {values[p.productId] || "—"}
                        </td>
                      ))}
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </>
      )}

      <div style={{ marginTop: "20px", display: "flex", gap: "12px" }}>
        <button className="back-btn" onClick={() => navigate("/")}>
          Назад
        </button>
        <button
          className="back-btn"
          style={{ backgroundColor: "#dc3545" }}
          onClick={() => {
            localStorage.removeItem("compare");
            window.location.reload();
          }}
        >
          Очистить сравнение
        </button>
      </div>
    </div>
  );
};

export default ComparePage;
