import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';


export default function Register() {
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const password = watch("password"); // Для проверки совпадения паролей

  const onSubmit = async (data) => {
    try {
      await registerUser(data);
      alert('Регистрация успешна!');
    } catch (error) {
      alert('Ошибка: ' + error.message);
    }
  };

  return (
    <div className="auth-form">
      <h1>РЕГИСТРАЦИЯ</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {/* Email */}
        <input
          {...register("email", { required: "Обязательное поле" })}
          placeholder="E-mail"
        />
        {errors.email && <span className="error">{errors.email.message}</span>}

        {/* ФИО */}
        <input
          {...register("fullName", { required: "Обязательное поле" })}
          placeholder="ФИО"
        />
        {errors.fullName && <span className="error">{errors.fullName.message}</span>}

        {/* Пароль */}
        <input
          type="password"
          {...register("password", { required: "Обязательное поле", minLength: 6 })}
          placeholder="Пароль"
        />
        {errors.password && <span className="error">Пароль должен быть не менее 6 символов</span>}

        {/* Повтор пароля */}
        <input
          type="password"
          {...register("confirmPassword", {
            validate: (value) => value === password || "Пароли не совпадают",
          })}
          placeholder="Повторить пароль"
        />
        {errors.confirmPassword && (
          <span className="error">{errors.confirmPassword.message}</span>
        )}

        <button type="submit">Отправить</button>
      </form>

      <div className="auth-links">
        <Link to="/login">Уже есть аккаунт? Войти</Link>
        <Link to="/forgot-password">Забыли пароль?</Link>
      </div>
    </div>
  );
}
