import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import './CategoryPage/';

const Televisions = () => {
  const allTelevisions = [
    {
      id: 10,
      img: "/img/tovar/oled_tv.jpg",
      model: "OLED55C3",
      brand: "LG",
      oldPrice: 2000,
      newPrice: 1800,
      availability: "В наличии",
      specs: {
        screen: "55'' OLED",
        resolution: "4K"
      }
    },
    {
      id: 11,
      img: "/img/tovar/qled_tv.jpg",
      model: "QLED Q80C",
      brand: "Samsung",
      oldPrice: 1900,
      newPrice: 1700,
      availability: "В наличии",
      specs: {
        screen: "65'' QLED",
        resolution: "4K"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    resolution: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === 'reset') {
      setFilters({
        brand: "",
        screenSize: "",
        resolution: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  const filteredTelevisions = allTelevisions.filter((tv) => {
    const price = tv.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;
    return (
      (!filters.brand || tv.brand === filters.brand) &&
      (!filters.screenSize || tv.specs.screen.includes(filters.screenSize)) &&
      (!filters.resolution || tv.specs.resolution === filters.resolution) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Телевизоры" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог телевизоров</h1>
        <div className="product-grid">
          {filteredTelevisions.map((tv) => (
            <Link to={`/products/${tv.id}`} key={tv.id} className="product-link">
              <ProductCard
                id={tv.id}
                img={tv.img}
                model={tv.model}
                oldPrice={tv.oldPrice}
                newPrice={tv.newPrice}
                availability={tv.availability}
                discount={tv.oldPrice ? Math.round((1 - tv.newPrice / tv.oldPrice) * 100) : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Televisions;
