// ProductCard.jsx
import React from 'react';
import './ProductCard.css';

const ProductCard = ({
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  isNew,
  availability
}) => {
  return (
    <div className="product-card">
      <div className="card-header">
        {isNew && <span className="badge new">NEW</span>}
        {discount && <span className="badge discount">{discount}</span>}
        <img src={img} alt={model} className="product-image" />
      </div>

      <div className="card-body">
        <h3 className="product-title">{model}</h3>

        <div className="price-section">
          {oldPrice && (
            <div className="price-row old-price">
              <span>{oldPrice.toLocaleString()} B</span>
              <span className="storage">128GB</span>
            </div>
          )}
          <div className="price-row new-price">
            <span>{newPrice.toLocaleString()} BYN</span>
            <span className="storage">{oldPrice ? '256GB' : '128GB'}</span>
          </div>
        </div>
      </div>

      <div className="card-footer">
        <span className={`availability ${availability.toLowerCase()}`}>
          {availability}
        </span>
        <button className="cart-btn">
          <span>В корзину</span>
          <svg class="icon" viewBox="0 0 24 24">
          <path xmlns="http://www.w3.org/2000/svg" fill-rule="evenodd" clip-rule="evenodd" d="M0 1.5C0 0.947715 0.447715 0.5 1 0.5H1.24897C2.34933 0.5 3.32531 1.09612 3.84754 2H20.6819C22.5138 2 23.9179 3.62751 23.6495 5.43965L22.2421 14.9396C22.0242 16.4106 20.7615 17.5 19.2745 17.5H6.75103C5.24743 17.5 3.97607 16.3869 3.77735 14.8965L2.2402 3.36784C2.17395 2.87103 1.75017 2.5 1.24897 2.5H1C0.447715 2.5 0 2.05228 0 1.5ZM4.34218 4L5.75981 14.6322C5.82605 15.129 6.24983 15.5 6.75103 15.5H19.2745C19.7702 15.5 20.191 15.1369 20.2637 14.6465L21.6711 5.14655C21.7606 4.5425 21.2925 4 20.6819 4H4.34218ZM7 20C6.44772 20 6 20.4477 6 21C6 21.5523 6.44772 22 7 22C7.55228 22 8 21.5523 8 21C8 20.4477 7.55228 20 7 20ZM4 21C4 19.3431 5.34315 18 7 18C8.65685 18 10 19.3431 10 21C10 22.6569 8.65685 24 7 24C5.34315 24 4 22.6569 4 21ZM18 21C18 20.4477 18.4477 20 19 20C19.5523 20 20 20.4477 20 21C20 21.5523 19.5523 22 19 22C18.4477 22 18 21.5523 18 21ZM19 18C17.3431 18 16 19.3431 16 21C16 22.6569 17.3431 24 19 24C20.6569 24 22 22.6569 22 21C22 19.3431 20.6569 18 19 18Z" fill="white"/>
          </svg>
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
