import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phone: '',
    birthDate: '',
  });
  const [orders, setOrders] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return navigate("/login");

    const fetchProfile = async () => {
      try {
        const [userRes, ordersRes, favRes] = await Promise.all([
          fetch(`http://localhost:5085/api/users/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/favorite/${storedUser.userId}`),
        ]);

        if (!userRes.ok || !ordersRes.ok || !favRes.ok) throw new Error("Ошибка загрузки");

        const user = await userRes.json();
        setUserData({
          username: user.username || '',
          email: user.email || '',
          password: user.password || '',
          firstName: user.firstName || '',
          lastName: user.lastName || '',
          phone: user.phone || '',
          birthDate: user.birthDate?.slice(0, 10) || '',
        });

        const orderData = await ordersRes.json();
        const favoriteData = await favRes.json();

        const parsedOrders = orderData.map(order => ({
          id: order.orderId,
          date: order.createdAt,
          totalPrice: order.totalAmount,
          items: order.items?.map(item => ({
            id: item.id,
            productId: item.product?.productId || item.productId,
            name: item.product?.model || item.product?.name || 'Без названия',
            image: item.product?.imageUrl || '/img/placeholder.png',
            price: item.product?.newPrice || item.product?.price || 0,
            quantity: item.quantity || 1,
          })) || [],
        }));

        setOrders(parsedOrders);
        setFavorites(favoriteData);
        setLoading(false);
      } catch (err) {
        console.error("Ошибка:", err);
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const handleChange = e => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/users/${storedUser.userId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: storedUser.userId, ...userData }),
      });

      if (res.ok) {
        const updated = await res.json();
        localStorage.setItem('user', JSON.stringify(updated));
        alert('Профиль обновлён');
      } else {
        alert('Ошибка при обновлении профиля');
      }
    } catch {
      alert('Ошибка при сохранении');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleRemoveFavorite = async (productId) => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/favorite/${storedUser.userId}/${productId}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        setFavorites(prev => prev.filter(fav => fav.productId !== productId));
        alert('Удалено из избранного');
      }
    } catch {
      alert('Ошибка при удалении');
    }
  };

  if (loading) return <div style={{ textAlign: 'center', fontSize: '18px' }}>Загрузка...</div>;

  return (
    <div className="profile-wrapper">
      <h2 className="profile-title">Личный кабинет</h2>

      <div className="profile-form">
        <div className="form-group"><label>Имя пользователя</label><input type="text" name="username" value={userData.username} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Email</label><input type="email" name="email" value={userData.email} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Пароль</label><input type="password" name="password" value={userData.password} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Имя</label><input type="text" name="firstName" value={userData.firstName} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Фамилия</label><input type="text" name="lastName" value={userData.lastName} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Телефон</label><input type="tel" name="phone" value={userData.phone} onChange={handleChange} className="checkout-input" /></div>
        <div className="form-group"><label>Дата рождения</label><input type="date" name="birthDate" value={userData.birthDate} onChange={handleChange} className="checkout-input" /></div>

        <div className="profile-actions">
          <button className="checkout-btn" onClick={handleSave}>Сохранить</button>
          <button className="logout-btn" onClick={handleLogout}>Выйти</button>
        </div>
      </div>

      <div className="profile-section-title">Мои заказы</div>
      {orders.length === 0 ? (
        <p style={{ textAlign: 'center' }}>У вас нет заказов.</p>
      ) : (
        orders.map(order => (
          <div key={order.id}>
            <h4>Заказ №{order.id} от {new Date(order.date).toLocaleDateString()} — {order.totalPrice.toLocaleString()} BYN</h4>
            <div className="cart-grid">
              {order.items.map(item => (
                <div key={item.id} className="cart-item" onClick={() => navigate(`/products/${item.productId}`)}>
                  <img src={item.image} alt={item.name} className="cart-image" />
                  <h3 className="cart-title">{item.name}</h3>
                  <div className="new-price">{item.price.toLocaleString()} BYN</div>
                  <div className="quantity">Количество: {item.quantity}</div>
                  <button className="view-product-btn" onClick={(e) => { e.stopPropagation(); navigate(`/products/${item.productId}`); }}>Подробнее</button>
                </div>
              ))}
            </div>
          </div>
        ))
      )}

      <div className="profile-section-title">Избранное</div>
      {favorites.length === 0 ? (
        <p style={{ textAlign: 'center', fontSize: '16px' }}>Нет избранных товаров.</p>
      ) : (
        <div className="cart-grid">
          {favorites.map(fav => {
            const product = fav.product;
            const imageUrl = product?.imageUrl || '/img/placeholder.png';
            const title = product?.model || product?.name || 'Без названия';
            const price = product?.newPrice || 0;

            return (
              <div
                key={fav.productId}
                className="cart-item"
                onClick={() => navigate(`/products/${fav.productId}`)}
              >
                <div className="cart-image-wrapper">
                  <img src={imageUrl} alt={title} className="cart-image" />
                </div>
                <h3 className="cart-title">{title}</h3>
                <div className="new-price">{price.toLocaleString()} BYN</div>
                <button className="remove-favorite-btn" onClick={(e) => { e.stopPropagation(); handleRemoveFavorite(fav.productId); }}>Удалить</button>
                <button className="view-product-btn" onClick={(e) => { e.stopPropagation(); navigate(`/products/${fav.productId}`); }}>Подробнее</button>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
