import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({
  id,
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  availability
}) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [isCompared, setIsCompared] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const compare = JSON.parse(localStorage.getItem("compare")) || [];
    setIsCompared(compare.includes(id));

    const user = JSON.parse(localStorage.getItem("user"));
    if (user?.userId) {
      fetch(`http://localhost:5085/api/favorite/${user.userId}`)
        .then(res => res.json())
        .then(data => {
          const isInFavorites = data.some(fav => fav.productId === id);
          setIsFavorite(isInFavorites);
        })
        .catch(err => console.error("Ошибка получения избранного:", err));
    }
  }, [id]);

  const handleCardClick = () => {
    navigate(`/products/${id}`);
  };

 const handleAddToCart = async () => {
  const user = JSON.parse(localStorage.getItem("user"));
  if (!user?.userId) return alert("Сначала войдите в аккаунт");

  try {
    const res = await fetch("http://localhost:5085/api/cart/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        userId: user.userId,
        productId: product.productId,
        quantity: 1
      })
    });

    if (!res.ok) {
      const text = await res.text(); // ⚠️ пытаемся прочитать как текст
      console.error("Ошибка от сервера:", text);
      alert("Ошибка сервера при добавлении в корзину");
      return;
    }

    const result = await res.json();
    if (result.success) {
      alert("Товар добавлен в корзину!");
    } else {
      alert("Ошибка: " + JSON.stringify(result));
    }
  } catch (error) {
    console.error("Ошибка корзины:", error);
    alert("Произошла ошибка при добавлении в корзину");
  }
};

  const handleCompare = (e) => {
    e.stopPropagation();
    const compare = JSON.parse(localStorage.getItem("compare")) || [];

    if (!compare.includes(id)) {
      compare.push(id);
      localStorage.setItem("compare", JSON.stringify(compare));
      setIsCompared(true);
      alert("Товар добавлен к сравнению");
    } else {
      compare.splice(compare.indexOf(id), 1);
      localStorage.setItem("compare", JSON.stringify(compare));
      setIsCompared(false);
      alert("Товар удалён из сравнения");
    }
  };

  const handleToggleFavorite = async (e) => {
    e.stopPropagation();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) {
      alert("Пожалуйста, войдите в аккаунт");
      navigate('/login');
      return;
    }

    try {
      const url = isFavorite
        ? `http://localhost:5085/api/favorite/${user.userId}/${id}`
        : "http://localhost:5085/api/favorite";
      const method = isFavorite ? "DELETE" : "POST";

      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: method === "POST" ? JSON.stringify({ userId: user.userId, productId: id }) : null
      });

      if (res.ok) {
        setIsFavorite(!isFavorite);
        alert(isFavorite ? "Удалено из избранного" : "Добавлено в избранное");
      } else {
        const errorData = await res.json();
        alert(`Ошибка: ${errorData.message || "Не удалось обновить избранное"}`);
      }
    } catch (err) {
      console.error("Ошибка при работе с избранным:", err);
      alert("Произошла ошибка");
    }
  };

  return (
    <div className="product-card" onClick={handleCardClick} style={{ cursor: 'pointer' }}>
      <div className="product-image-wrapper">
        {discount && <div className="discount-badge">-{discount}%</div>}
        {availability === "В наличии" && <div className="availability-badge in-stock">В наличии</div>}
        <img src={img} alt={model} className="product-image" />
      </div>

      <div className="product-content">
        <h3 className="product-title">{model}</h3>

        <div className="price-wrapper">
          {oldPrice && oldPrice > newPrice && (
            <div className="old-price">{oldPrice.toLocaleString()} ₽</div>
          )}
          <div className="new-price">{newPrice.toLocaleString()} ₽</div>
        </div>

        <div className="product-footer">
          <button
            className="add-to-cart-btn"
            onClick={handleAddToCart}
          >
            В корзину
          </button>

          <button
            className="add-to-cart-btn"
            style={{
              backgroundColor: isCompared ? "#28a745" : "#555",
              fontWeight: isCompared ? "bold" : "normal"
            }}
            onClick={handleCompare}
          >
            {isCompared ? "В сравнении" : "Сравнить"}
          </button>

          <button
            className={`favorite-button ${isFavorite ? 'active' : ''}`}
            onClick={handleToggleFavorite}
          >
            <svg width="24" height="24" viewBox="0 0 24 24">
              <path
                d="M17.612 2.826C17.172 2.405 16.65 2.072 16.075 1.844C15.5 1.617 14.884 1.5 14.262 1.5C13.64 1.5 13.024 1.617 12.449 1.844C11.874 2.072 11.352 2.405 10.912 2.826L10 3.698L9.087 2.826C8.198 1.977 6.994 1.5 5.737 1.5C4.48 1.5 3.276 1.977 2.388 2.826C1.499 3.675 1 4.826 1 6.027C1 7.227 1.499 8.378 2.388 9.227L3.3 10.099L10 16.5L16.699 10.099L17.612 9.227C18.052 8.807 18.401 8.308 18.639 7.759C18.877 7.21 19 6.621 19 6.027C19 5.432 18.877 4.843 18.639 4.294C18.401 3.745 18.052 3.246 17.612 2.826Z"
                fill={isFavorite ? "#FF4444" : "none"}
                stroke={isFavorite ? "#FF4444" : "#0C0C0C"}
                strokeWidth="2"
              />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
