import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import '../CategoryPage/CategoryPage';

const Speakers = () => {
  const allSpeakers = [
    {
      id: 12,
      img: "/img/tovar/jblgo4.jpg",
      model: "JBL Go 4",
      brand: "JBL",
      oldPrice: 80,
      newPrice: 60,
      availability: "В наличии",
      specs: {
        type: "Портативные"
      }
    },
    {
      id: 13,
      img: "/img/tovar/bose.jpg",
      model: "SoundLink Flex",
      brand: "Bose",
      oldPrice: 150,
      newPrice: 120,
      availability: "В наличии",
      specs: {
        type: "Портативные"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    type: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === 'reset') {
      setFilters({
        brand: "",
        type: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  const filteredSpeakers = allSpeakers.filter((speaker) => {
    const price = speaker.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;
    return (
      (!filters.brand || speaker.brand === filters.brand) &&
      (!filters.type || speaker.specs.type === filters.type) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Колонки" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог колонок</h1>
        <div className="product-grid">
          {filteredSpeakers.map((speaker) => (
            <Link to={`/products/${speaker.id}`} key={speaker.id} className="product-link">
              <ProductCard
                id={speaker.id}
                img={speaker.img}
                model={speaker.model}
                oldPrice={speaker.oldPrice}
                newPrice={speaker.newPrice}
                availability={speaker.availability}
                discount={speaker.oldPrice ? Math.round((1 - speaker.newPrice / speaker.oldPrice) * 100) : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Speakers;
