import React, { useEffect, useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import { Link } from 'react-router-dom';
import '../CategoryPage/CategoryPage';
const Smartphones = () => {
  const [smartphones, setSmartphones] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    availability: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  useEffect(() => {
    const fetchSmartphones = async () => {
      const query = new URLSearchParams({
        category: 'Смартфоны',
        brand: filters.brand,
        minPrice: filters.minPrice,
        maxPrice: filters.maxPrice
      });

      const response = await fetch(`/api/products?${query.toString()}`);
      const data = await response.json();
      setSmartphones(data);
    };

    fetchSmartphones();
  }, [filters]);

  return (
    <div className="smartphones-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="smartphones-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {smartphones.map(phone => (
            <Link to={`/products/${phone.id}`} key={phone.id} className="product-link">
              <ProductCard
                {...phone}
                discount={phone.oldPrice && phone.oldPrice > phone.price ? Math.round((1 - phone.price / phone.oldPrice) * 100) : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
