import React from 'react';
import './ProductCard.css';

const ProductCard = ({ img, model, price, availability }) => {
  return (
    <div className="product-card">
      <div className="product-image">
        <img src={img} alt={model} />
      </div>
      <h3 className="product-model">{model}</h3>
      <div className="product-prices">
        <span className="new-price">{price.toLocaleString()} ₽</span>
      </div>
      <div className={`availability ${availability === "В наличии" ? 'in-stock' : 'out-of-stock'}`}>
        {availability}
      </div>
      <button className="add-to-cart" disabled={availability !== "В наличии"}>
        {availability === "В наличии" ? 'В корзину' : 'Недоступно'}
      </button>
    </div>
  );
};

export default ProductCard;
