// Header.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [isCatalogOpen, setIsCatalogOpen] = useState(false);
  const catalogRef = useRef(null);

  // –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ –¥–ª—è –≤—ã–ø–∞–¥–∞—é—â–µ–≥–æ –º–µ–Ω—é
  const categories = [
    "–≠–ª–µ–∫—Ç—Ä–æ–Ω–∏–∫–∞",
    "–û–¥–µ–∂–¥–∞",
    "–ë—ã—Ç–æ–≤–∞—è —Ç–µ—Ö–Ω–∏–∫–∞",
    "–ö—Ä–∞—Å–æ—Ç–∞",
    "–°–ø–æ—Ä—Ç",
    "–î–µ—Ç—Å–∫–∏–µ —Ç–æ–≤–∞—Ä—ã",
    "–ö–Ω–∏–≥–∏"
  ];

  // –ó–∞–∫—Ä—ã—Ç–∏–µ –º–µ–Ω—é –ø—Ä–∏ –∫–ª–∏–∫–µ –≤–Ω–µ –æ–±–ª–∞—Å—Ç–∏
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (catalogRef.current && !catalogRef.current.contains(e.target)) {
        setIsCatalogOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <header className="header">
      <div className="logo">
        <Link to="/">
          <h1>COOLZY</h1>
        </Link>
      </div>

      <div className="search">
        <input
          type="text"
          className="search_box"
          placeholder="üîç –ü–û–ò–°–ö"
        />
      </div>

      <nav className="navigation">
        {/* –ö–Ω–æ–ø–∫–∞ –∫–∞—Ç–∞–ª–æ–≥–∞ —Å –≤—ã–ø–∞–¥–∞—é—â–∏–º –º–µ–Ω—é */}
        <div className="catalog-wrapper" ref={catalogRef}>
          <button
            className="catalog"
            onClick={() => setIsCatalogOpen(!isCatalogOpen)}
          >
            <img
              src="./img/icon/catalog_icon.jpg"
              alt="–ö–∞—Ç–∞–ª–æ–≥"
              className="catalog-icon"
            />
            <span className="icon_text">–ö–∞—Ç–∞–ª–æ–≥</span>
          </button>

          {/* –í—ã–ø–∞–¥–∞—é—â–µ–µ –º–µ–Ω—é */}
          <div className={`catalog-menu ${isCatalogOpen ? 'show' : ''}`}>
            {categories.map((category, index) => (
              <Link
                to={`/category/${category}`}
                key={index}
                className="menu-item"
              >
                {category}
              </Link>
            ))}
          </div>
        </div>

        {/* –ö–æ—Ä–∑–∏–Ω–∞ (–±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π) */}
        <Link to="/cart" className="basket">
          <img src="/img/icon/icon_basket.jpg" alt="–ö–æ—Ä–∑–∏–Ω–∞" />
          <span className="icon_text">–ö–æ—Ä–∑–∏–Ω–∞</span>
        </Link>

        {/* –ü—Ä–æ—Ñ–∏–ª—å (–æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–∞—è –≤–µ—Ä—Å–∏—è) */}
        <Link to="/profile" className="profile">
          <img src="/img/icon/icon_profile.jpg" alt="–ü—Ä–æ—Ñ–∏–ª—å" />
          <span className="icon_text">–ü—Ä–æ—Ñ–∏–ª—å</span>
        </Link>
      </nav>
    </header>
  );
}

export default Header;
