// Category.jsx
import React from 'react';
import './Category.css';

// Map categories to their image sources (replace with actual image paths)
const categoryImages = {
  Смартфоны: './img/huaweipura70.jng',
  Ноутбуки: '/images/macbook.png',
  Планшеты: '/images/tablets.png',
  Телевизоры: '/images/TVHorizont.jng',
  Колонки: '/images/jblgo4.jng',
};

const Category = ({ setCategory }) => {
  const categories = ['Смартфоны', 'Ноутбуки', 'Планшеты', 'Телевизоры', 'Колонки'];

  return (
    <div className="category-wrapper">
      <h3 className="category-title">Категории</h3>
      <div className="category-container">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setCategory(category)}
            className="category-card"
          >
            <img src={categoryImages[category]} alt={category} />
            <p>{category}</p>
          </button>
        ))}
      </div>
    </div>
  );
};

export default Category;
