// OrderSuccess.jsx
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './OrderSuccess.css';

const OrderSuccess = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { orderId, pdfUrl, contractUrl } = location.state || {};

  return (
    <div className="order-success-container">
      <div className="order-success-box">
        <h2>Спасибо за заказ!</h2>
        {orderId ? (
          <p>Ваш номер заказа: <strong>#{orderId}</strong></p>
        ) : (
          <p>Заказ успешно оформлен.</p>
        )}
        {pdfUrl && (
          <a
            href={pdfUrl}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: "inline-block",
              padding: "10px 20px",
              backgroundColor: "#4CAF50",
              color: "white",
              borderRadius: "5px",
              margin: "10px",
              textDecoration: "none"
            }}
          >
            Скачать гарантийный талон (PDF)
          </a>
        )}
        {contractUrl && (
          <a
            href={contractUrl}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: "inline-block",
              padding: "10px 20px",
              backgroundColor: "#007BFF",
              color: "white",
              borderRadius: "5px",
              margin: "10px",
              textDecoration: "none"
            }}
          >
            Скачать договор (PDF)
          </a>
        )}
        <button onClick={() => navigate('/')} className="success-btn">
          Вернуться в каталог
        </button>
      </div>
    </div>
  );
};

export default OrderSuccess;
