import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Favorites.css';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) {
      setError("Пожалуйста, войдите в аккаунт");
      setLoading(false);
      return;
    }

    setLoading(true);
    fetch(`http://localhost:5085/api/favorite/${user.userId}`)
      .then(res => {
        if (!res.ok) throw new Error("Ошибка загрузки избранного");
        return res.json();
      })
      .then(data => {
        setFavorites(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const handleRemoveFavorite = async (productId) => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/favorite/${user.userId}/${productId}`, {
        method: "DELETE"
      });

      if (res.ok) {
        setFavorites(favorites.filter(fav => fav.productId !== productId));
        alert("Товар удалён из избранного");
      } else {
        alert("Ошибка при удалении из избранного");
      }
    } catch (err) {
      console.error("Ошибка при удалении:", err);
      alert("Произошла ошибка");
    }
  };

  if (loading) {
    return <p style={{ textAlign: "center", fontSize: "18px" }}>Загрузка...</p>;
  }

  if (error) {
    return <p style={{ textAlign: "center", fontSize: "18px", color: "red" }}>{error}</p>;
  }

  if (favorites.length === 0) {
    return <p style={{ textAlign: "center", fontSize: "18px" }}>Нет избранных товаров</p>;
  }

  return (
    <div className="favorites-container">
      <h2 style={{ marginBottom: "20px" }}>Избранные товары</h2>
      <div className="cart-grid">
        {favorites.map(fav => (
          <div
            key={fav.id}
            className="cart-item"
            style={{ cursor: "pointer" }}
          >
            <div className="cart-image-wrapper">
              <img
                src={fav.product?.images?.[0]?.imageUrl || "/img/placeholder.png"}
                alt={fav.product?.name || "Товар"}
                className="cart-image"
              />
            </div>
            <h3 className="cart-title">{fav.product?.name || "Без названия"}</h3>
            <div className="new-price">
              {fav.product?.price.toLocaleString() || "0"} BYN
            </div>
            <button
              className="remove-favorite-btn"
              onClick={(e) => {
                e.stopPropagation();
                handleRemoveFavorite(fav.productId);
              }}
            >
              Удалить
            </button>
            <button
              className="view-product-btn"
              onClick={() => navigate(`/products/${fav.productId}`)}
            >
              Подробнее
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Favorites;
