import { useEffect, useState } from "react";
import axios from "axios";

export default function ProductManager() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [editId, setEditId] = useState(null);

  const [form, setForm] = useState({
    model: "",
    brand: "",
    newPrice: "",
    availability: "–í –Ω–∞–ª–∏—á–∏–∏",
    categoryId: "",
    image: null,
  });

  useEffect(() => {
    loadProducts();
    loadCategories();
  }, []);

  const loadProducts = () => {
    axios.get("http://localhost:5085/api/products").then(res => setProducts(res.data));
  };

  const loadCategories = () => {
    axios.get("http://localhost:5085/api/categories").then(res => setCategories(res.data));
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      setForm({ ...form, image: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("model", form.model);
    data.append("brand", form.brand);
    data.append("newPrice", form.newPrice);
    data.append("availability", form.availability);
    data.append("categoryId", form.categoryId);
    if (form.image) data.append("image", form.image);

    try {
      if (editId) {
        await axios.put(`http://localhost:5085/api/admin/products/${editId}`, data);
        alert("–¢–æ–≤–∞—Ä –æ–±–Ω–æ–≤–ª—ë–Ω");
      } else {
        await axios.post("http://localhost:5085/api/admin/products/add", data);
        alert("–¢–æ–≤–∞—Ä –¥–æ–±–∞–≤–ª–µ–Ω");
      }
      setForm({ model: "", brand: "", newPrice: "", availability: "–í –Ω–∞–ª–∏—á–∏–∏", categoryId: "", image: null });
      setEditId(null);
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å —Ç–æ–≤–∞—Ä?")) return;
    try {
      await axios.delete(`http://localhost:5085/api/admin/products/${id}`);
      loadProducts();
    } catch (err) {
      console.error(err);
      alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏");
    }
  };

  return (
    <div style={{ maxWidth: "900px" }}>
      <h2 style={{ marginBottom: 20 }}>{editId ? "‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Ç–æ–≤–∞—Ä" : "üì¶ –î–æ–±–∞–≤–∏—Ç—å –Ω–æ–≤—ã–π —Ç–æ–≤–∞—Ä"}</h2>

      <form onSubmit={handleSubmit} style={{ display: "grid", gap: 12, marginBottom: 30 }}>
        <input name="model" value={form.model} onChange={handleChange} placeholder="–ú–æ–¥–µ–ª—å" required />
        <input name="brand" value={form.brand} onChange={handleChange} placeholder="–ë—Ä–µ–Ω–¥" required />
        <input name="newPrice" type="number" value={form.newPrice} onChange={handleChange} placeholder="–¶–µ–Ω–∞" required />
        <select name="availability" value={form.availability} onChange={handleChange}>
          <option value="–í –Ω–∞–ª–∏—á–∏–∏">–í –Ω–∞–ª–∏—á–∏–∏</option>
          <option value="–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏">–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏</option>
        </select>
        <select name="categoryId" value={form.categoryId} onChange={handleChange} required>
          <option value="">-- –í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é --</option>
          {categories.map(cat => (
            <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
          ))}
        </select>
        <input type="file" name="image" onChange={handleChange} accept="image/*" />
        <button type="submit">{editId ? "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏—è" : "–î–æ–±–∞–≤–∏—Ç—å —Ç–æ–≤–∞—Ä"}</button>
      </form>

      <h3>üìã –°–ø–∏—Å–æ–∫ —Ç–æ–≤–∞—Ä–æ–≤</h3>
      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr style={{ background: "#f0f0f0" }}>
            <th>ID</th><th>–ú–æ–¥–µ–ª—å</th><th>–ë—Ä–µ–Ω–¥</th><th>–¶–µ–Ω–∞</th><th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th><th>–î–µ–π—Å—Ç–≤–∏—è</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.productId}>
              <td>{p.productId}</td>
              <td>{p.model}</td>
              <td>{p.brand}</td>
              <td>{p.newPrice} —Ä—É–±.</td>
              <td>{p.category?.name || "-"}</td>
              <td>
                <button onClick={() => {
                  setEditId(p.productId);
                  setForm({
                    model: p.model,
                    brand: p.brand,
                    newPrice: p.newPrice,
                    availability: p.availability,
                    categoryId: p.categoryId,
                    image: null,
                  });
                  window.scrollTo({ top: 0, behavior: "smooth" });
                }} style={{ marginRight: 10, background: "#3b82f6", color: "#fff", border: "none", padding: "5px 10px", borderRadius: 4 }}>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å</button>

                <button onClick={() => handleDelete(p.productId)} style={{ background: "red", color: "#fff", border: "none", padding: "5px 10px", borderRadius: 4 }}>
                  –£–¥–∞–ª–∏—Ç—å
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
