import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      const user = JSON.parse(localStorage.getItem("user"));
      if (!user?.userId) return;

      try {
        const res = await fetch(`http://localhost:5085/api/cart/${user.userId}`);
        const data = await res.json();

        setCartItems(data.map(item => ({
          id: item.id,
          name: item.product.name,
          image: item.product.images?.[0]?.imageUrl || '',
          price: item.product.price,
          discount: 0,
          quantity: item.quantity,
          available: item.product.quantityInStock > 0
        })));
      } catch (err) {
        console.error("Ошибка загрузки корзины", err);
      }
    };

    fetchCart();
  }, []);

  const handleRemove = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleQuantityChange = (id, amount) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = Math.max(1, item.quantity + amount);
        return { ...item, quantity: newQuantity };
      }
      return item;
    }));
  };

  const calculateTotal = () => {
    return cartItems.reduce(
      (sum, item) => sum + (item.price - (item.discount || 0)) * item.quantity,
      0
    );
  };

  const handleCheckout = () => {
    navigate('/checkout', {
      state: {
        cartItems,
        total: calculateTotal()
      }
    });
  };

  if (cartItems.length === 0) {
    return (
      <div className="cart-container">
        <h2>Моя корзина</h2>
        <div className="cart-empty">
          <p>Ваша корзина пуста</p>
          <button className="catalog-btn" onClick={() => navigate('/')}>Перейти в каталог</button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>
      <div className="cart-grid">
        {cartItems.map(item => (
          <div key={item.id} className="cart-item">
            <div className="cart-image-wrapper">
              <img src={item.image} alt={item.name} className="cart-image" />
            </div>
            <div className="cart-content">
              <h3 className="cart-title">{item.name}</h3>
              <div className="price-wrapper">
                <div className="new-price">{item.price.toLocaleString()} BYN</div>
                <div className="quantity-control">
                  <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
                </div>
              </div>
            </div>
            <button className="remove-btn" onClick={() => handleRemove(item.id)} title="Удалить">×</button>
          </div>
        ))}
      </div>
      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{calculateTotal().toLocaleString()} BYN</span>
        </div>
        <button className="checkout-btn" onClick={handleCheckout}>Перейти к оформлению</button>
      </div>
    </div>
  );
};

export default Cart;
