import React from 'react';
import './Category.css';

const Category = () => {
  const categories = [
    { img: "/img/category/macbook.jpg", name: "Ноутбуки" },
    { img: "/img/category/huaweipura70.jpg", name: "Смартфоны" },
    { img: "/img/category/TVHorizont.jpg", name: "Телевизоры" },
    { img: "/img/category/notepad.jpg", name: "Планшеты" },
    { img: "/img/category/jblgo4.jpg", name: "Колонки" }
  ];

  return (
    <div className="category">
      <h2 className="category-title">Каталог</h2>
      <div className="category-list">
        {categories.map((category, index) => (
          <div className="category-card" key={index}>
            <div className="category-image">
              <img src={category.img} alt={category.name} />
            </div>
            <p className="category-name">{category.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Category;
