import React from 'react';
import './ProductCard.css';

const ProductCard = ({ img, model, oldPrice, newPrice, discount, availability }) => {
  const calculateDiscount = () => {
    if (!oldPrice || oldPrice <= newPrice) return null;
    return Math.round(((oldPrice - newPrice) / oldPrice) * 100);
  };

  return (
    <div className="product-card">
      <div className="product-image-wrapper">
        {discount && (
          <div className="discount-badge">
            {calculateDiscount() || discount}%
          </div>
        )}
        <img src={img} alt={model} className="product-image" />
      </div>

      <div className="product-content">
        <h3 className="product-title">{model}</h3>

        <div className="price-wrapper">
          {oldPrice && oldPrice > newPrice && (
            <span className="old-price">{oldPrice.toLocaleString()} BYN</span>
          )}
          <span className="new-price">{newPrice.toLocaleString()} BYN</span>
        </div>

        <div className="product-footer">
          <span className={`availability-badge ${availability === "В наличии" ? 'in-stock' : 'out-of-stock'}`}>
            {availability}
          </span>
          <button className="cart-button">
            <svg className="cart-icon" viewBox="0 0 24 24">
              <path d="M6 6h15l-1.5 9h-13L4 4H2" stroke="currentColor" strokeWidth="2"/>
              <circle cx="9" cy="20" r="1" fill="currentColor"/>
              <circle cx="18" cy="20" r="1" fill="currentColor"/>
            </svg>
            В корзину
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
