// LoginForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    console.log("Отправка логина:", { email, password });

    try {
      // Проверка доступности сервера перед отправкой
      const response = await axios.get('http://localhost:5085/api/auth/ping', {
        headers: { 'Content-Type': 'application/json' },
      });
      console.log("Сервер доступен:", response.data);

      // Отправка запроса логина
      const loginResponse = await axios.post(
        'http://localhost:5085/api/auth/login',
        { email, password },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );
      console.log("Успешный вход. Ответ сервера:", loginResponse.data);
      const user = loginResponse.data;
      localStorage.setItem('user', JSON.stringify(user));

      if (user.isAdmin) {
        navigate('/admin');
      } else {
        navigate('/profile');
      }
    } catch (err) {
      console.error('Ошибка логина:', err.response?.data || err.message);
      const errorMessage = err.response?.data?.message ||
                          err.response?.data ||
                          'Сервер недоступен или неверные данные. Проверьте подключение и попробуйте снова.';
      setError(errorMessage);
    }
  };

  return (
    <div className="auth-wrapper">
      <form onSubmit={handleSubmit} className="auth-form">
        <h2 className="auth-title">Вход в аккаунт</h2>

        <label className="auth-label">Email</label>
        <input
          type="email"
          className="auth-input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <label className="auth-label">Пароль</label>
        <input
          type="password"
          className="auth-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {error && <div className="auth-error">{error}</div>}

        <button type="submit" className="auth-button">Войти</button>
        <button
          type="button"
          className="auth-link"
          onClick={() => navigate('/forgot-password')}
        >
          Забыли пароль?
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
