import { useEffect, useState } from "react";
import axios from "axios";

export default function UsersPanel() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    load();
  }, []);

  const load = () => {
    axios.get("http://localhost:5085/api/admin/users")
      .then(res => setUsers(res.data));
  };

  const handleDelete = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è?")) return;
    await axios.delete(`http://localhost:5085/api/admin/users/${id}`);
    load();
  };

  return (
    <div style={{ maxWidth: "1000px", margin: "0 auto", padding: "30px" }}>
      <h2>üë§ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</h2>
      <table style={{ width: "100%", borderCollapse: "collapse", fontSize: "14px" }}>
        <thead>
          <tr style={{ backgroundColor: "#f3f4f6" }}>
            <th style={th}>ID</th>
            <th style={th}>Email</th>
            <th style={th}>–ò–º—è</th>
            <th style={th}>–ê–≤–∞—Ç–∞—Ä</th>
            <th style={th}>–î–µ–π—Å—Ç–≤–∏—è</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.userId}>
              <td style={td}>{u.userId}</td>
              <td style={td}>{u.email}</td>
              <td style={td}>{u.username}</td>
              <td style={td}>
                <img src={u.avatar || "/img/placeholder.png"} alt="avatar" width={40} height={40} style={{ borderRadius: "50%" }} />
              </td>
              <td style={td}>
                <button onClick={() => handleDelete(u.userId)} style={deleteBtn}>üóë</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

const th = { padding: "10px", borderBottom: "2px solid #ddd", textAlign: "left" };
const td = { padding: "10px", borderBottom: "1px solid #eee" };
const deleteBtn = {
  background: "#ef4444", color: "#fff", border: "none", padding: "6px 10px",
  borderRadius: "6px", cursor: "pointer"
};
