// CategoryPage.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import ProductCard from '../Components/ProductCard/ProductCard';
import Filters from '../Components/Filters/Filters';
import "/"

const CategoryPage = ({ category }) => {
  const [products, setProducts] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    availability: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (name, value) => {
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  useEffect(() => {
    const fetchProducts = async () => {
      const query = new URLSearchParams({
        category,
        brand: filters.brand,
        minPrice: filters.minPrice,
        maxPrice: filters.maxPrice
      });

      const response = await fetch(`/api/products?${query.toString()}`);
      const data = await response.json();
      setProducts(data);
    };

    fetchProducts();
  }, [category, filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог {category}</h1>
        <div className="product-grid">
          {products.map(product => (
            <Link to={`/products/${product.id}`} key={product.id} className="product-link">
              <ProductCard
                img={product.imageUrl}
                model={product.name}
                oldPrice={product.oldPrice}
                newPrice={product.price}
                availability={product.isAvailable ? 'В наличии' : 'Нет в наличии'}
                discount={product.oldPrice && product.oldPrice > product.price
                  ? Math.round((1 - product.price / product.oldPrice) * 100)
                  : null}
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CategoryPage;
