import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProductById } from "../../api";
import "./ProductPageStyles.css";

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [comment, setComment] = useState("");
  const [selectedColor, setSelectedColor] = useState(null);

  useEffect(() => {
    getProductById(productId).then((data) => {
      setProduct(data);
      setSelectedColor(data.colors?.[0]); // Устанавливаем первый цвет по умолчанию
    });

    fetch(`http://localhost:5085/api/reviews/${productId}`)
      .then(async (res) => {
        if (!res.ok) return [];
        try {
          return await res.json();
        } catch {
          return [];
        }
      })
      .then(setReviews)
      .catch(() => setReviews([]));
  }, [productId]);

  const handleAddToCart = async () => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.userId) return alert("Сначала войдите в аккаунт");

    try {
      const res = await fetch("http://localhost:5085/api/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.userId,
          productId: product.id,
          quantity: 1,
        }),
      });

      if (!res.ok) {
        const text = await res.text();
        return alert("Ошибка: " + text);
      }

      const data = await res.json();
      if (data.success) alert("Товар добавлен в корзину");
    } catch (error) {
      console.error("Ошибка корзины", error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user?.username) return alert("Войдите в аккаунт");

    const review = {
      productId: parseInt(productId),
      userName: user.username,
      comment,
    };

    try {
      const res = await fetch("http://localhost:5085/api/reviews/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(review),
      });

      if (!res.ok) return alert("Ошибка при отправке отзыва");

      setComment("");
      const updated = await fetch(`http://localhost:5085/api/reviews/${productId}`);
      const data = await updated.json();
      setReviews(data);
    } catch (err) {
      console.error("Ошибка отправки отзыва", err);
    }
  };

  if (!product) return <div className="error">Загрузка...</div>;

  const averageRating = reviews.length
    ? (reviews.reduce((sum, r) => sum + (r.rating || 4), 0) / reviews.length).toFixed(1)
    : 4; // Средний рейтинг по умолчанию, если отзывов нет

  return (
    <div className="product-page-container">
      <div className="product-main-content">
        <div className="product-left-column">
          <div className="product-image-wrapper">
            <img
              src={selectedColor?.imageUrl || product.images[0]?.imageUrl || "https://via.placeholder.com/500x500"}
              alt={product.name}
              className="product-main-image"
            />
            <div className="availability-badge in-stock">В наличии</div>
          </div>
          <div className="additional-images">
            {product.images.map((img, index) => (
              <img
                key={index}
                src={img.imageUrl}
                alt={`${product.name} ${index + 1}`}
                className="additional-image"
              />
            ))}
          </div>
        </div>

        <div className="product-center-column">
          <div className="product-details">
            <h1 className="product-title">{product.name}</h1>

            <div className="price-wrapper">
              <span className="new-price">{product.price} BYN</span>
              {product.oldPrice && (
                <span className="old-price">{product.oldPrice} BYN</span>
              )}
            </div>

            <div className="color-options">
              {product.colors?.map((color, index) => (
                <button
                  key={index}
                  className={`color-btn ${selectedColor === color ? "selected" : ""}`}
                  style={{ backgroundColor: color.hex }}
                  onClick={() => setSelectedColor(color)}
                />
              ))}
            </div>

            <div className="product-specs">
              <h3>Характеристики</h3>
              <div className="specs-grid">
                {product.description.split(",").map((item, index) => {
                  const [title, ...rest] = item.split(":");
                  const value = rest.join(":").trim();
                  return (
                    <div className="spec-item" key={index}>
                      <span className="spec-title">{title.trim()}</span>
                      <span className="spec-value">{value}</span>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>

        <div className="price-floating-container">
          <div className="price-section">
            <div>
              <span className="product-price">{product.price} BYN</span>
              {product.oldPrice && (
                <span className="old-price"> {product.oldPrice} BYN</span>
              )}
            </div>
            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              В корзину
            </button>
          </div>
        </div>
      </div>

      <div className="product-reviews">
        <h3>Отзывы</h3>
        <p>{reviews.length} отзывов • Средняя оценка: {averageRating} ★</p>
        <form onSubmit={handleReviewSubmit} className="review-form">
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            placeholder="Оставьте ваш отзыв"
            required
            rows="4"
          />
          <button type="submit" className="add-to-cart-btn">
            Отправить отзыв
          </button>
        </form>

        {reviews.length === 0 ? (
          <p className="no-reviews">Отзывов пока нет</p>
        ) : (
          <ul className="review-list">
            {reviews.map((r) => (
              <li key={r.id} className="review-item">
                <div className="review-header">
                  <strong>{r.userName}</strong>
                  <span className="review-rating">
                    {"★".repeat(r.rating || 4)}
                    {"☆".repeat(5 - (r.rating || 4))}
                  </span>
                </div>
                <p>{r.comment}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ProductPage;
