import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import '../CategoryPage/CategoryPage';

const Laptops = () => {
  const allLaptops = [
    {
      id: 4,
      img: "../../img/tovar/iphon.jpg",
      model: "MacBook Air M2",
      brand: "Apple",
      oldPrice: 1800,
      newPrice: 1500,
      availability: "В наличии",
      specs: {
        screen: "13.6'' Retina",
        cores: "8-ядерный M2",
        ram: "8GB",
        storage: "512GB"
      }
    },
    {
      id: 5,
      img: "/img/tovar/dell.jpg",
      model: "XPS 13",
      brand: "Dell",
      oldPrice: 1600,
      newPrice: 1300,
      availability: "В наличии",
      specs: {
        screen: "13.4'' OLED",
        cores: "12-ядерный Intel i7",
        ram: "16GB",
        storage: "1TB"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    processor: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === 'reset') {
      setFilters({
        brand: "",
        screenSize: "",
        processor: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  const filteredLaptops = allLaptops.filter((laptop) => {
    const price = laptop.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;
    return (
      (!filters.brand || laptop.brand === filters.brand) &&
      (!filters.screenSize || laptop.specs.screen.includes(filters.screenSize.replace('"', ''))) &&
      (!filters.processor || laptop.specs.cores.includes(filters.processor)) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Ноутбуки" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог ноутбуков</h1>
        <div className="product-grid">
          {filteredLaptops.map((laptop) => (
            className="product-link">
              <ProductCard
                id={laptop.id}
                img={laptop.img}
                model={laptop.model}
                oldPrice={laptop.oldPrice}
                newPrice={laptop.newPrice}
                availability={laptop.availability}
                discount={laptop.oldPrice ? Math.round((1 - laptop.newPrice / laptop.oldPrice) * 100) : null}
              />
            
          ))}
        </div>
      </div>
    </div>
  );
};

export default Laptops;
