      <p>–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞: <strong>#{orderId}</strong></p>

      <div className="order-links">
        {pdfUrl && (
          <a href={pdfUrl} target="_blank" rel="noreferrer" className="link-btn">üìÑ –°–∫–∞—á–∞—Ç—å –≥–∞—Ä–∞–Ω—Ç–∏—é</a>
        )}
        {contractUrl && (
          <a href={contractUrl} target="_blank" rel="noreferrer" className="link-btn">üìÉ –°–∫–∞—á–∞—Ç—å –¥–æ–≥–æ–≤–æ—Ä</a>
        )}
      </div>

      <button onClick={() => navigate("/profile")} className="checkout-btn">
        –í –ø—Ä–æ—Ñ–∏–ª—å
      </button>
    </div>
  );
};

export default OrderSuccess;
