import React from 'react';
import './ProductCard.css';

const ProductCard = ({ img, model, oldPrice, newPrice, availability }) => {
  return (
    <div className="product-card">
      <div className="product-image">
        <img src={img} alt={model} />
      </div>
      <h3 className="product-model">{model}</h3>
      <div className="product-prices">
        {oldPrice && <span className="old-price">{oldPrice.toLocaleString()} ₽</span>}
        <span className="new-price">{newPrice.toLocaleString()} ₽</span>
      </div>
      <div className={`availability ${availability ? 'in-stock' : 'out-of-stock'}`}>
        {availability ? 'В наличии' : 'Нет в наличии'}
      </div>
      <button className="add-to-cart" disabled={!availability}>
        {availability ? 'В корзину' : 'Недоступно'}
      </button>
    </div>
  );
};

export default ProductCard;
