// Filters.jsx
import React from 'react';
import './Filters.css';

const filterConfigs = {
  'Смартфоны': [
    { type: 'select', key: 'brand', label: 'Бренд', options: ['Apple', 'Samsung', 'Xiaomi', 'Huawei', 'Honor'] },
    { type: 'select', key: 'ram', label: 'Оперативная память', options: ['2GB', '4GB', '6GB', '8GB', '12GB'] },
    { type: 'select', key: 'storage', label: 'Встроенная память', options: ['64GB', '128GB', '256GB', '512GB', '1TB'] },
    { type: 'range', key: 'price', label: 'Цена, ₽', min: 0, max: 100000 },
  ],
  'Ноутбуки': [
    { type: 'select', key: 'brand', label: 'Бренд', options: ['Apple', 'Dell', 'Lenovo', 'HP', 'Asus'] },
    { type: 'select', key: 'screenSize', label: 'Размер экрана', options: ['13"', '14"', '15"', '17"'] },
    { type: 'select', key: 'processor', label: 'Процессор', options: ['Intel i5', 'Intel i7', 'M1', 'M2'] },
    { type: 'range', key: 'price', label: 'Цена, ₽', min: 0, max: 2000 },
  ],
  'Планшеты': [
    { type: 'select', key: 'brand', label: 'Бренд', options: ['Apple', 'Samsung', 'Lenovo', 'Huawei'] },
    { type: 'select', key: 'screenSize', label: 'Размер экрана', options: ['10"', '11"', '12"', '13"'] },
    { type: 'range', key: 'price', label: 'Цена, ₽', min: 0, max: 1500 },
  ],
  'Телевизоры': [
    { type: 'select', key: 'brand', label: 'Бренд', options: ['LG', 'Samsung', 'Sony', 'Philips'] },
    { type: 'select', key: 'screenSize', label: 'Размер экрана', options: ['32"', '43"', '55"', '65"'] },
    { type: 'select', key: 'resolution', label: 'Разрешение', options: ['Full HD', '4K', '8K'] },
    { type: 'range', key: 'price', label: 'Цена, ₽', min: 0, max: 2500 },
  ],
  'Колонки': [
    { type: 'select', key: 'brand', label: 'Бренд', options: ['JBL', 'Bose', 'Sony', 'Anker'] },
    { type: 'select', key: 'type', label: 'Тип', options: ['Портативные', 'Настольные'] },
    { type: 'range', key: 'price', label: 'Цена, ₽', min: 0, max: 200 },
  ],
};

const Filters = ({ category, filters, onFilterChange }) => {
  const categoryFilters = filterConfigs[category] || [];

  const renderFilter = (filter) => {
    switch (filter.type) {
      case 'select':
        return (
          <div className="filter-group" key={filter.key}>
            <label className="filter-label">{filter.label}</label>
            <select
              value={filters[filter.key] || ''}
              onChange={(e) => onFilterChange(filter.key, e.target.value)}
            >
              <option value="">Все</option>
              {filter.options.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>
        );
      case 'range':
        return (
          <div className="filter-group" key={filter.key}>
            <label className="filter-label">{filter.label}</label>
            <div className="price-inputs">
              <input
                type="number"
                placeholder="От"
                value={filters.minPrice || ''}
                onChange={(e) => onFilterChange('minPrice', e.target.value)}
                min={filter.min}
                max={filter.max}
              />
              <span>—</span>
              <input
                type="number"
                placeholder="До"
                value={filters.maxPrice || ''}
                onChange={(e) => onFilterChange('maxPrice', e.target.value)}
                min={filter.min}
                max={filter.max}
              />
            </div>
            <div className="price-error">Укажите корректный диапазон цен</div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="filters-panel">
      <h3>Фильтры</h3>
      {categoryFilters.map(renderFilter)}
      <button className="reset-filters" onClick={() => onFilterChange('reset', null)}>
        Сбросить фильтры
      </button>
    </div>
  );
};

export default Filters;
