import React, { useState } from 'react';
import ProductCard from '../components/ProductCard/ProductCard';
import Filters from '../components/Filters/Filters';
import './Smartphones.css';

const Smartphones = () => {
  const allSmartphones = [
    { id: 1, img: "/img/tovar/Iphone16Pro.jpeg", model: "iPhone 16 Pro", brand: "Apple", price: 120000, availability: "В наличии" },
    { id: 2, img: "/img/tovar/iphone12.jpg", model: "iPhone 12", brand: "Apple", price: 60000, availability: "Нет в наличии" },
    { id: 3, img: "/img/tovar/samsungS23.jpg", model: "Galaxy S23", brand: "Samsung", price: 80000, availability: "В наличии" },
    { id: 4, img: "/img/tovar/xiaomi14.jpg", model: "Xiaomi 14", brand: "Xiaomi", price: 50000, availability: "В наличии" },
    // Добавляешь сюда свои смартфоны
  ];

  const [filters, setFilters] = useState({
    brand: '',
    availability: '',
    minPrice: '',
    maxPrice: ''
  });

  const handleFilterChange = (filterName, value) => {
    setFilters(prev => ({
      ...prev,
      [filterName]: value
    }));
  };

  const filteredSmartphones = allSmartphones.filter(phone => {
    const matchesBrand = filters.brand ? phone.brand === filters.brand : true;
    const matchesAvailability = filters.availability ? phone.availability === filters.availability : true;
    const matchesMinPrice = filters.minPrice ? phone.price >= Number(filters.minPrice) : true;
    const matchesMaxPrice = filters.maxPrice ? phone.price <= Number(filters.maxPrice) : true;
    return matchesBrand && matchesAvailability && matchesMinPrice && matchesMaxPrice;
  });

  return (
    <div className="smartphones-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="smartphones-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {filteredSmartphones.map(phone => (
            <ProductCard key={phone.id} {...phone} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
