import React, { useState, useEffect } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import "../CategoryPage/CategoryPage";
import { getProductsByCategory } from "../../api";

const Speakers = () => {
  const [speakers, setSpeakers] = useState([]);
  const [filters, setFilters] = useState({
    brand: "",
    type: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === "reset") {
      setFilters({
        brand: "",
        type: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  useEffect(() => {
    const fetchSpeakers = async () => {
      const data = await getProductsByCategory("Колонки", filters);
      setSpeakers(data);
    };
    fetchSpeakers();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Колонки" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог колонок</h1>
        <div className="product-grid">
          {speakers.map((speaker) => (
            <Link to={`/products/${speaker.id}`} key={speaker.id} className="product-link">
              <ProductCard
                id={speaker.id}
                img={speaker.img}
                model={speaker.model}
                oldPrice={speaker.oldPrice}
                newPrice={speaker.newPrice}
                availability={speaker.availability}
                discount={
                  speaker.oldPrice
                    ? Math.round((1 - speaker.newPrice / speaker.oldPrice) * 100)
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Speakers;
