import React, { useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import './Smartphones.css';

const Smartphones = () => {
  // Тестовые данные для проверки
  const allSmartphones = [
    {
      id: 1,
      img: "/img/tovar/iphone14.jpg",
      model: "Смартфон Apple iPhone 14 128GB",
      oldPrice: 88999,
      newPrice: 84999,
      discount: "4%",
      isNew: true,
      availability: "В наличии"
    },
    {
      id: 2,
      img: "/img/tovar/iphone13.jpg",
      model: "Смартфон Apple iPhone 13 128GB",
      oldPrice: 79999,
      newPrice: 75999,
      availability: "В наличии"
    },
    {
      id: 3,
      img: "/img/tovar/iphone13pro.jpg",
      model: "Смартфон Apple iPhone 13 Pro",
      newPrice: 11999,
      availability: "В наличии"
    },
    // ... остальные товары
  ];


  const [filters, setFilters] = useState({
    brand: '',
    availability: '',
    minPrice: '',
    maxPrice: ''
  });

  // Добавьте этот console.log для отладки
  console.log('Rendered smartphones:', allSmartphones);

  const handleFilterChange = (filterName, value) => {
    setFilters(prev => ({ ...prev, [filterName]: value }));
  };

  const filteredSmartphones = allSmartphones.filter(phone => {
    return (
      (!filters.brand || phone.brand === filters.brand) &&
      (!filters.availability || phone.availability === filters.availability) &&
      (!filters.minPrice || phone.price >= Number(filters.minPrice)) &&
      (!filters.maxPrice || phone.price <= Number(filters.maxPrice))
    );
  });

  return (
    <div className="smartphones-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="smartphones-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {filteredSmartphones.length > 0 ? (
            filteredSmartphones.map(phone => (
              <ProductCard key={phone.id} {...phone} />
            ))
          ) : (
            <p className="empty-message">Товары не найдены</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;
