import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const CartPage = () => {
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      name: "Смартфон Apple iPhone 14 128GB",
      image: "https://store.storeimages.cdn-apple.com/4668/as-images.apple.com/is/iphone-14-finish-select-202209-6-1inch-blue?wid=5120&hei=2880&fmt=p-jpg&qlt=80&.v=1661026582322",
      screen: "6.1 (2532x1170)",
      cores: 6,
      power: "20 Вт",
      price: 84999,
      discount: 2500,
      quantity: 1,
      available: true
    },
    {
      id: 2,
      name: "Ноутбук MacBook Air M1",
      image: "https://www.apple.com/v/macbook-air/m1/images/overview/hero_endframe__ea0qze85eyi6_large.jpg",
      screen: "13.3 (2560x1600)",
      cores: 8,
      power: "30 Вт",
      price: 74999,
      discount: 1500,
      quantity: 1,
      available: true
    }
  ]);

  const navigate = useNavigate();

  const handleRemove = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const handleQuantityChange = (id, amount) => {
    setCartItems(cartItems.map(item => {
      if (item.id === id) {
        const newQuantity = Math.max(1, item.quantity + amount);
        return { ...item, quantity: newQuantity };
      }
      return item;
    }));
  };

  const handleCheckout = () => {
    navigate('/checkout', {
      state: {
        cartItems,
        total: calculateTotal()
      }
    });
  };

  const calculateTotal = () => {
    return cartItems.reduce(
      (sum, item) => sum + (item.price - (item.discount || 0)) * item.quantity,
      0
    );
  };

  if (cartItems.length === 0) {
    return (
      <div className="cart-container">
        <h2>Моя корзина</h2>
        <div className="cart-empty">
          <p>Ваша корзина пуста</p>
          <button
            className="catalog-btn"
            onClick={() => navigate('/')}
          >
            Перейти в каталог
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Моя корзина</h2>

      {cartItems.map(item => (
        <div key={item.id} className="cart-item">
          <div className="item-content">
            <img
              src={item.image || 'https://via.placeholder.com/120x120?text=No+Image'}
              alt={item.name}
              className="item-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/120x120?text=No+Image';
              }}
            />
            <div className="item-info">
              <h3>{item.name}</h3>
              <p>Экран: {item.screen}</p>
              <p>Количество ядер: {item.cores}</p>
              <p>Мощность блока питания: {item.power}</p>
              <div className="availability">
                {item.available ? "В наличии" : "Нет в наличии"}
              </div>
            </div>
          </div>

          <div className="item-price">
            <div className="price-row">
              <span className="original-price">
                {item.price.toLocaleString()} BYN
              </span>
              {item.discount > 0 && (
                <span className="discount">
                  - {item.discount.toLocaleString()} BYN
                </span>
              )}
            </div>

            <div className="quantity-control">
              <button onClick={() => handleQuantityChange(item.id, -1)}>-</button>
              <span>{item.quantity}</span>
              <button onClick={() => handleQuantityChange(item.id, 1)}>+</button>
            </div>

            <div className="item-total">
              {((item.price - (item.discount || 0)) * item.quantity).toLocaleString()} BYN
            </div>
          </div>

          <button
            className="remove-btn"
            onClick={() => handleRemove(item.id)}
            title="Удалить"
          >
            ×
          </button>
        </div>
      ))}

      <div className="cart-summary">
        <div className="summary-row">
          <span>Итого:</span>
          <span>{calculateTotal().toLocaleString()} BYN</span>
        </div>
        <button
          className="checkout-btn"
          onClick={handleCheckout}
        >
          Перейти к оформлению
        </button>
      </div>
    </div>
  );
};

export default CartPage;
