import React, { useState, useEffect } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";
import { Link } from "react-router-dom";
import "../CategoryPage/CategoryPage";
import { getProductsByCategory } from "../../api";

const Televisions = () => {
  const [televisions, setTelevisions] = useState([]);
  const [filters, setFilters] = useState({
    brand: "",
    screenSize: "",
    resolution: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    if (filterName === "reset") {
      setFilters({
        brand: "",
        screenSize: "",
        resolution: "",
        minPrice: "",
        maxPrice: ""
      });
    } else {
      setFilters((prev) => ({ ...prev, [filterName]: value }));
    }
  };

  useEffect(() => {
    const fetchTelevisions = async () => {
      const data = await getProductsByCategory("Телевизоры", filters);
      setTelevisions(data);
    };
    fetchTelevisions();
  }, [filters]);

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Телевизоры" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог телевизоров</h1>
        <div className="product-grid">
          {televisions.map((tv) => (
            <Link to={`/products/${tv.id}`} key={tv.id} className="product-link">
              <ProductCard
                id={tv.id}
                img={tv.img}
                model={tv.model}
                oldPrice={tv.oldPrice}
                newPrice={tv.newPrice}
                availability={tv.availability}
                discount={
                  tv.oldPrice
                    ? Math.round((1 - tv.newPrice / tv.oldPrice) * 100)
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Televisions;
