import { useEffect, useState } from "react";
import axios from "axios";

export default function ProductManager() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    model: "",
    brand: "",
    newPrice: "",
    availability: "В наличии",
    image: null
  });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    load();
  }, []);

  const load = () => {
    axios.get("http://localhost:5085/api/admin/products")
      .then(res => setProducts(res.data));
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "image") {
      setForm({ ...form, image: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("model", form.model);
    data.append("brand", form.brand);
    data.append("newPrice", form.newPrice);
    data.append("availability", form.availability);
    if (form.image) data.append("image", form.image);

    try {
      if (editId) {
        await axios.put(`http://localhost:5085/api/admin/products/${editId}`, data);
        alert("Обновлено");
      } else {
        await axios.post("http://localhost:5085/api/admin/products/add", data);
        alert("Добавлено");
      }
      setForm({ model: "", brand: "", newPrice: "", availability: "В наличии", image: null });
      setEditId(null);
      load();
    } catch (err) {
      console.error(err);
      alert("Ошибка");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Удалить товар?")) return;
    await axios.delete(`http://localhost:5085/api/admin/products/${id}`);
    load();
  };

  return (
    <div>
      <h2>{editId ? "Редактировать товар" : "Добавить товар"}</h2>
      <form onSubmit={handleSubmit} style={{ display: "grid", gap: 10, maxWidth: 500 }}>
        <input name="model" value={form.model} onChange={handleChange} placeholder="Модель" />
        <input name="brand" value={form.brand} onChange={handleChange} placeholder="Бренд" />
        <input name="newPrice" value={form.newPrice} onChange={handleChange} placeholder="Цена" />
        <select name="availability" value={form.availability} onChange={handleChange}>
          <option value="В наличии">В наличии</option>
          <option value="Нет в наличии">Нет в наличии</option>
        </select>
        <input type="file" name="image" onChange={handleChange} />
        <button type="submit">{editId ? "Сохранить" : "Добавить"}</button>
      </form>

      <h3>Список товаров</h3>
      <table>
        <thead>
          <tr>
            <th>Фото</th>
            <th>Модель</th>
            <th>Бренд</th>
            <th>Цена</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.productId}>
              <td>
                <img src={p.images?.[0]?.imageUrl || "/img/placeholder.png"} width="60" height="60" />
              </td>
              <td>{p.model}</td>
              <td>{p.brand}</td>
              <td>{p.newPrice} руб.</td>
              <td>
                <button onClick={() => {
                  setEditId(p.productId);
                  setForm({
                    model: p.model,
                    brand: p.brand,
                    newPrice: p.newPrice,
                    availability: p.availability,
                    image: null
                  });
                }}>Редакт</button>
                <button onClick={() => handleDelete(p.productId)} style={{ marginLeft: 10 }}>Удалить</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
