import { useEffect, useState } from "react";
import axios from "axios";

export default function AdminPanel() {
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    axios.get("/api/admin/users").then(res => setUsers(res.data));
    axios.get("/api/products").then(res => setProducts(res.data));
    axios.get("/api/orders").then(res => setOrders(res.data)).catch(() => setOrders([]));
  }, []);

  // Updated styles for a modern look
  const sectionStyle = {
    marginTop: "40px",
    padding: "20px",
    background: "#ffffff",
    borderRadius: "8px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)",
    border: "1px solid #e5e7eb",
  };

  const tableStyle = {
    width: "100%",
    borderCollapse: "separate",
    borderSpacing: "0",
    marginTop: "15px",
    fontSize: "14px",
    fontFamily: "'Inter', sans-serif",
  };

  const thStyle = {
    backgroundColor: "#f3f4f6",
    padding: "12px 16px",
    textAlign: "left",
    borderBottom: "2px solid #d1d5db",
    fontWeight: "600",
    color: "#374151",
    textTransform: "uppercase",
    fontSize: "12px",
  };

  const tdStyle = {
    padding: "12px 16px",
    borderBottom: "1px solid #e5e7eb",
    color: "#4b5563",
  };

  const rowStyle = {
    transition: "background-color 0.2s ease",
  };

  const rowHoverStyle = {
    backgroundColor: "#f9fafb",
  };

  const availabilityStyle = (availability) => ({
    padding: "4px 8px",
    borderRadius: "12px",
    fontSize: "12px",
    display: "inline-block",
    backgroundColor: availability === "В наличии" ? "#dcfce7" : "#fee2e2",
    color: availability === "В наличии" ? "#15803d" : "#dc2626",
  });

  return (
    <div style={{ padding: "40px", maxWidth: "1200px", margin: "0 auto" }}>
      <h1 style={{ marginBottom: "40px", fontSize: "28px", fontWeight: "700", color: "#111827" }}>
        Админ-панель
      </h1>

      {/* Пользователи */}
      <section style={sectionStyle}>
        <h2 style={{ fontSize: "20px", fontWeight: "600", color: "#111827" }}>👤 Пользователи</h2>
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Имя</th>
              <th style={thStyle}>Email</th>
              <th style={thStyle}>Роль</th>
            </tr>
          </thead>
          <tbody>
            {users.map(u => (
              <tr
                key={u.userId}
                style={rowStyle}
                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = rowHoverStyle.backgroundColor)}
                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "transparent")}
              >
                <td style={tdStyle}>{u.userId}</td>
                <td style={tdStyle}>{u.username}</td>
                <td style={tdStyle}>{u.email}</td>
                <td style={tdStyle}>{u.isAdmin ? "Админ" : "Пользователь"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Товары */}
      <section style={sectionStyle}>
        <h2 style={{ fontSize: "20px", fontWeight: "600", color: "#111827" }}>📦 Товары</h2>
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Модель</th>
              <th style={thStyle}>Бренд</th>
              <th style={thStyle}>Цена</th>
              <th style={thStyle}>Наличие</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr
                key={p.productId}
                style={rowStyle}
                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = rowHoverStyle.backgroundColor)}
                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "transparent")}
              >
                <td style={tdStyle}>{p.productId}</td>
                <td style={tdStyle}>{p.model}</td>
                <td style={tdStyle}>{p.brand}</td>
                <td style={tdStyle}>{p.newPrice} руб.</td>
                <td style={tdStyle}>
                  <span style={availabilityStyle(p.availability)}>{p.availability}</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>

      {/* Заказы */}
      <section style={sectionStyle}>
        <h2 style={{ fontSize: "20px", fontWeight: "600", color: "#111827" }}>🧾 Заказы</h2>
        {orders.length === 0 ? (
          <p style={{ padding: "10px", color: "#6b7280" }}>Нет заказов или ошибка при загрузке.</p>
        ) : (
          <table style={tableStyle}>
            <thead>
              <tr>
                <th style={thStyle}>ID</th>
                <th style={thStyle}>Пользователь</th>
                <th style={thStyle}>Дата</th>
                <th style={thStyle}>Сумма</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(o => (
                <tr
                  key={o.orderId}
                  style={rowStyle}
                  onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = rowHoverStyle.backgroundColor)}
                  onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "transparent")}
                >
                  <td style={tdStyle}>{o.orderId}</td>
                  <td style={tdStyle}>{o.user?.username || "–"}</td>
                  <td style={tdStyle}>{new Date(o.orderDate).toLocaleString()}</td>
                  <td style={tdStyle}>{o.totalAmount || "–"} руб.</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}
