import React, { useState } from 'react';
import ProductCard from '..///Components/ProductList/ProductList';
import './CatalogSmartphone.css';

const CatalogSmartphone = () => {
  const [filters, setFilters] = useState({
    storage: [],
    ram: [],
    brand: [],
    cores: []
  });

  const products = [
    // Ваш массив товаров из примера
    {id: 1, name: 'Apple iPhone 14 128GB', price: 84999, storage: 128, ram: 6, brand: 'Apple', cores: 6},
    {id: 2, name: 'HUAWEI nova Y61', price: 11999, storage: 64, ram: 4, brand: 'Huawei', cores: 4},
    // ... остальные товары
  ];

  const handleFilterChange = (type, value) => {
    setFilters(prev => ({
      ...prev,
      [type]: prev[type].includes(value)
        ? prev[type].filter(item => item !== value)
        : [...prev[type], value]
    }));
  };

  const filteredProducts = products.filter(product => {
    return (
      (filters.storage.length === 0 || filters.storage.includes(product.storage)) &&
      (filters.ram.length === 0 || filters.ram.includes(product.ram)) &&
      (filters.brand.length === 0 || filters.brand.includes(product.brand)) &&
      (filters.cores.length === 0 || filters.cores.includes(product.cores))
    );
  });

  const FilterSection = ({title, type, options}) => (
    <div className="filter-section">
      <h3>{title}</h3>
      {options.map(option => (
        <label key={option} className="filter-option">
          <input
            type="checkbox"
            checked={filters[type].includes(option)}
            onChange={() => handleFilterChange(type, option)}
          />
          {option}
        </label>
      ))}
    </div>
  );

  return (
    <div className="catalog-page">
      <div className="filters-column">
        <h2>Фильтры</h2>
        <FilterSection
          title="Встроенная память"
          type="storage"
          options={[32, 64, 128, 256, 312]}
        />
        <FilterSection
          title="Оперативная память"
          type="ram"
          options={[1, 1.5, 2]}
        />
        <FilterSection
          title="Бренд"
          type="brand"
          options={['Apple', 'Honor', 'Samsung', 'Huawei']}
        />
        <FilterSection
          title="Количество ядер"
          type="cores"
          options={[4, 6, 9]}
        />
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <ProductCard
            key={product.id}
            name={product.name}
            price={product.price}
            onAddToCart={() => console.log('Added to cart:', product.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default CatalogSmartphone;
