// Laptops.jsx
import React, { useState } from "react";
import ProductCard from "../../Components/ProductCard/ProductCard";
import Filters from "../../Components/Filters/Filters";

import { Link } from "react-router-dom";

const Laptops = () => {
  const allLaptops = [
    {
      id: 4,
      img: "../../img/tovar/macbook.jpg",
      model: "MacBook Air M2",
      brand: "Apple",
      oldPrice: 1800,
      newPrice: 1500,
      availability: "В наличии",
      memory: "512GB",
      specs: {
        screen: "13.6'' Retina",
        cores: "8-ядерный M2",
        power: "35W",
        ram: "8GB",
        storage: "512GB",
        camera: "1080p"
      }
    },
    {
      id: 5,
      img: "/img/tovar/dell.jpg",
      model: "XPS 13",
      brand: "Dell",
      oldPrice: 1600,
      newPrice: 1300,
      availability: "В наличии",
      memory: "1TB",
      specs: {
        screen: "13.4'' OLED",
        cores: "12-ядерный Intel i7",
        power: "45W",
        ram: "16GB",
        storage: "1TB",
        camera: "720p"
      }
    }
  ];

  const [filters, setFilters] = useState({
    brand: "",
    availability: "",
    minPrice: "",
    maxPrice: ""
  });

  const handleFilterChange = (filterName, value) => {
    setFilters((prev) => ({ ...prev, [filterName]: value }));
  };

  const filteredLaptops = allLaptops.filter((laptop) => {
    const price = laptop.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;

    return (
      (!filters.brand || laptop.brand === filters.brand) &&
      (!filters.availability || laptop.availability === filters.availability) &&
      price >= min &&
      price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters filters={filters} onFilterChange={handleFilterChange} />
      </div>

      <div className="category-catalog">
        <h1>Каталог ноутбуков</h1>
        <div className="product-grid">
          {filteredLaptops.map((laptop) => (
            <Link
              to={`/products/${laptop.id}`}
              key={laptop.id}
              className="product-link"
            >
              <ProductCard
                {...laptop}
                discount={
                  laptop.discount
                    ? `${Math.round(
                        (1 - laptop.newPrice / laptop.oldPrice) * 100
                      )}%`
                    : null
                }
              />
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Laptops;
