import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
 // Reuse Cart.css for consistent styling
import './ProfilePage.css'; // Keep existing ProfilePage.css for form-specific styles

const ProfilePage = () => {
  const [userData, setUserData] = useState({ username: '', email: '', password: '' });
  const [orders, setOrders] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser || !storedUser.userId) {
      navigate("/login");
      return;
    }

    const fetchProfile = async () => {
      try {
        const [userRes, ordersRes, favRes] = await Promise.all([
          fetch(`http://localhost:5085/api/users/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/favorite/${storedUser.userId}`),
        ]);

        if (!userRes.ok || !ordersRes.ok || !favRes.ok) {
          throw new Error("Ошибка загрузки данных профиля");
        }

        const userData = await userRes.json();
        const orderData = await ordersRes.json();
        const favoriteData = await favRes.json();

        setUserData({
          username: userData.username || '',
          email: userData.email || '',
          password: userData.password || '',
        });

        const parsedOrders = orderData.map(order => ({
          id: order.orderId,
          date: order.createdAt,
          totalPrice: order.totalAmount,
          items: order.items?.map(item => ({
            id: item.id,
            productId: item.product?.id || item.productId,
            name: item.product?.model || item.product?.name || 'Без названия',
            image: item.product?.images?.[0]?.imageUrl || '/img/placeholder.png',
            price: item.product?.newPrice || item.product?.price || 0,
            quantity: item.quantity || 1,
          })) || [],
        }));

        setOrders(parsedOrders);
        setFavorites(favoriteData);
        setLoading(false);
      } catch (err) {
        console.error("Ошибка загрузки профиля:", err);
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/users/${storedUser.userId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          id: storedUser.userId,
          ...userData,
        }),
      });

      if (res.ok) {
        const updated = await res.json();
        localStorage.setItem('user', JSON.stringify(updated));
        alert('Профиль обновлён');
      } else {
        const errText = await res.text();
        console.error('Ошибка обновления профиля:', errText);
        alert('Ошибка при обновлении профиля');
      }
    } catch (err) {
      console.error('Ошибка:', err);
      alert('Ошибка при сохранении');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleRemoveFavorite = async (productId) => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/favorite/${storedUser.userId}/${productId}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        setFavorites(favorites.filter(fav => fav.productId !== productId));
        alert('Товар удалён из избранного');
      } else {
        alert('Ошибка при удалении из избранного');
      }
    } catch (err) {
      console.error('Ошибка при удалении:', err);
      alert('Произошла ошибка');
    }
  };

  if (loading) {
    return <div style={{ textAlign: 'center', fontSize: '18px' }}>Загрузка...</div>;
  }

  return (
    <div className="cart-container">
      <h2>Профиль</h2>

      <div className="profile-form">
        <div className="form-group">
          <label>Имя пользователя</label>
          <input
            type="text"
            name="username"
            value={userData.username}
            onChange={handleChange}
            className="checkout-input"
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            className="checkout-input"
          />
        </div>

        <div className="form-group">
          <label>Пароль</label>
          <input
            type="password"
            name="password"
            value={userData.password}
            onChange={handleChange}
            className="checkout-input"
          />
        </div>

        <div className="profile-actions">
          <button className="checkout-btn" onClick={handleSave}>
            Сохранить
          </button>
          <button className="logout-btn" onClick={handleLogout}>
            Выйти
          </button>
        </div>
      </div>

      <div className="section">
        <h3>Мои заказы</h3>
        {orders.length === 0 ? (
          <p style={{ textAlign: 'center', fontSize: '18px' }}>У вас нет заказов.</p>
        ) : (
          orders.map(order => (
            <div key={order.id} className="order-section">
              <h4>
                Заказ №{order.id} от {new Date(order.date).toLocaleDateString()} —{' '}
                {order.totalPrice?.toLocaleString() || 0} BYN
              </h4>
              <div className="cart-grid">
                {order.items?.map(item => (
                  <div
                    key={item.id}
                    className="cart-item"
                    style={{ cursor: 'pointer' }}
                    onClick={() => navigate(`/products/${item.productId}`)}
                  >
                    <div className="cart-image-wrapper">
                      <img
                        src={item.image}
                        alt={item.name}
                        className="cart-image"
                      />
                    </div>
                    <h3 className="cart-title">{item.name}</h3>
                    <div className="new-price">
                      {item.price.toLocaleString()} BYN
                    </div>
                    <div className="quantity">Количество: {item.quantity}</div>
                    <button
                      className="view-product-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate(`/products/${item.productId}`);
                      }}
                    >
                      Подробнее
                    </button>
                  </div>
                ))}
              </div>
            </div>
          ))
        )}
      </div>

      <div className="section">
        <h3>Избранное</h3>
        {favorites.length === 0 ? (
          <p style={{ textAlign: 'center', fontSize: '18px' }}>Нет избранных товаров.</p>
        ) : (
          <div className="cart-grid">
            {favorites.map(fav => (
              <div
                key={fav.productId}
                className="cart-item"
                style={{ cursor: 'pointer' }}
                onClick={() => navigate(`/products/${fav.productId}`)}
              >
                <div className="cart-image-wrapper">
                  <img
                    src={fav.product?.images?.[0]?.imageUrl || '/img/placeholder.png'}
                    alt={fav.product?.name || 'Товар'}
                    className="cart-image"
                  />
                </div>
                <h3 className="cart-title">{fav.product?.name || 'Без названия'}</h3>
                <div className="new-price">
                  {fav.product?.price?.toLocaleString() || 0} BYN
                </div>
                <button
                  className="remove-favorite-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleRemoveFavorite(fav.productId);
                  }}
                >
                  Удалить
                </button>
                <button
                  className="view-product-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/products/${fav.productId}`);
                  }}
                >
                  Подробнее
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
