import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Checkout({ cartItems, clearCart }) {
  const [form, setForm] = useState({ name: "", address: "" });
  const navigate = useNavigate();
  const userId = localStorage.getItem("userId"); // ← из авторизации

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userId) {
      alert("Вы не авторизованы.");
      return;
    }

    const orderData = {
      userId,
      items: cartItems.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        price: item.newPrice
      })),
      name: form.name,
      address: form.address
    };

    try {
      const response = await fetch("http://localhost:5085/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(orderData)
      });

      if (!response.ok) throw new Error("Ошибка оформления");

      const result = await response.json();

      clearCart(); // очистка корзины
      navigate("/success", { state: { orderId: result.orderId } }); // ← передаём orderId
    } catch (err) {
      alert("Ошибка оформления заказа.");
      console.error(err);
    }
  };

  return (
    <div className="checkout-container">
      <h2>Оформление заказа</h2>
      <form onSubmit={handleSubmit} className="checkout-form">
        <input
          name="name"
          placeholder="Ваше имя"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="address"
          placeholder="Адрес доставки"
          value={form.address}
          onChange={handleChange}
          required
        />
        <button type="submit">Оформить</button>
      </form>
    </div>
  );
}
