// Smartphones.jsx
import React, { useEffect, useState } from 'react';
import ProductCard from '../../Components/ProductCard/ProductCard';
import Filters from '../../Components/Filters/Filters';
import '../CategoryPage/CategoryPage';

const Smartphones = () => {
  const [smartphones, setSmartphones] = useState([]);
  const [filters, setFilters] = useState({ brand: '', ram: '', storage: '', minPrice: '', maxPrice: '' });

  const handleFilterChange = (name, value) => {
    if (name === 'reset') setFilters({ brand: '', ram: '', storage: '', minPrice: '', maxPrice: '' });
    else setFilters(prev => ({ ...prev, [name]: value }));
  };

  useEffect(() => {
    const mockData = [
      { id: 1, img: "/img/tovar/iphone13.jpg", model: "iPhone 14 128GB", brand: "Apple", oldPrice: 84999, newPrice: 79999, availability: "В наличии", specs: { ram: "6GB", storage: "128GB" } },
      { id: 2, img: "/img/tovar/iphon12.jpg", model: "Galaxy S23 256GB", brand: "Samsung", oldPrice: 85999, newPrice: 80999, availability: "В наличии", specs: { ram: "8GB", storage: "256GB" } }
    ];
    setSmartphones(mockData);
  }, []);

  const filteredSmartphones = smartphones.filter(phone => {
    const price = phone.newPrice;
    const min = Number(filters.minPrice) || 0;
    const max = Number(filters.maxPrice) || Infinity;
    return (
      (!filters.brand || phone.brand === filters.brand) &&
      (!filters.ram || phone.specs.ram === filters.ram) &&
      (!filters.storage || phone.specs.storage === filters.storage) &&
      price >= min && price <= max
    );
  });

  return (
    <div className="category-page">
      <div className="filters-panel">
        <Filters category="Смартфоны" filters={filters} onFilterChange={handleFilterChange} />
      </div>
      <div className="category-catalog">
        <h1>Каталог смартфонов</h1>
        <div className="product-grid">
          {filteredSmartphones.map(phone => (
            <ProductCard
              key={phone.id}
              id={phone.id}
              img={phone.img}
              model={phone.model}
              oldPrice={phone.oldPrice}
              newPrice={phone.newPrice}
              availability={phone.availability}
              discount={phone.oldPrice > phone.newPrice ? Math.round((1 - phone.newPrice / phone.oldPrice) * 100) : null}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Smartphones;

