import React from 'react';
import { useParams } from 'react-router-dom';
import CustomButton from '../../Components/CustomButton/CustomButton';
import './ProductPage.css';

// Заглушка для отзывов
const reviews = [
  { id: 1, user: "Иван", rating: 5, text: "Отличный телефон, работает быстро!", date: "2025-05-20" },
  { id: 2, user: "Мария", rating: 4, text: "Камера супер, но батарея могла быть лучше.", date: "2025-05-18" },
];

const ProductPage = () => {
  const { productId } = useParams();
  const product = products.find(p => p.id === Number(productId));

  if (!product) {
    return <div className="error">Товар не найден</div>;
  }

  const handleAddToCart = () => {
    console.log(`Товар ${product.id} добавлен в корзину: ${product.model}`);
    // Здесь можно добавить реальную логику добавления в корзину
  };

  return (
    <div className="product-page-container">
      <div className="price-floating-container">
        <div className="price-section">
          <div className="product-price">
            {product.newPrice.toLocaleString()} BYN
          </div>
          <CustomButton
            className="add-to-cart-btn"
            onClick={handleAddToCart}
          >
            Добавить в корзину
          </CustomButton>
        </div>
      </div>

      <div className="product-main-content">
        <div className="product-left-column">
          <img
            src={process.env.PUBLIC_URL + product.image}
            alt={product.model}
            className="product-main-image"
          />
        </div>

        <div className="product-center-column">
          <div className="product-details">
            <div className="product-info">
              <h1 className="product-title">
                {product.model} {product.memory}
              </h1>

              <div className="product-specs">
                <h3>Характеристики</h3>
                <div className="specs-grid">
                  {Object.entries(product.specs).map(([key, value]) => (
                    <div className="spec-item" key={key}>
                      <span className="spec-title">
                        {key === 'screen' && 'Экран'}
                        {key === 'cores' && 'Процессор'}
                        {key === 'power' && 'Блок питания'}
                        {key === 'ram' && 'Оперативная память'}
                        {key === 'storage' && 'Встроенная память'}
                        {key === 'camera' && 'Камера'}
                      </span>
                      <span className="spec-value">{value}</span>
                    </div>
                  ))}
                </div>
              </div>

              <div className="product-reviews">
                <h3>Отзывы</h3>
                {reviews.length > 0 ? (
                  <div className="reviews-list">
                    {reviews.map(review => (
                      <div key={review.id} className="review-item">
                        <div className="review-header">
                          <span className="review-user">{review.user}</span>
                          <span className="review-rating">{'★'.repeat(review.rating)}</span>
                          <span className="review-date">{review.date}</span>
                        </div>
                        <p className="review-text">{review.text}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>Отзывов пока нет</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
