// Category.jsx
import React from "react";
import "./Category.css";

const categories = [
  { title: "Смартфоны", image: "/img/category/huaweipura70.jpg", link: "/catalog/smartphones" },
  { title: "Ноутбуки", image: "/img/category/macbook.jpg", link: "/catalog/laptops" },
  { title: "Компьютеры", image: "/img/category/pc.jpg", link: "/catalog/pcs" },
  { title: "Телевизоры", image: "/img/category/TVHorizont.jpg", link: "/catalog/tvs" },
  { title: "Планшеты", image: "/img/category/tablet.jpg", link: "/catalog/tablets" },
  { title: "Колонки", image: "/img/category/jblgo4.jpg", link: "/catalog/speakers" },
];

const Category = () => {
  return (
    <div className="category-wrapper">
      <h2 className="category-title">Каталог</h2>
      <div className="category-container">
        {categories.map((cat, index) => (
          <a href={cat.link} key={index} className="category-card">
            <img src={cat.image} alt={cat.title} />
            <p>{cat.title}</p>
          </a>
        ))}
      </div>
    </div>
  );
};

export default Category;
