import { useState, useEffect } from 'react';


export default function Profile() {
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    fullName: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    gender: '',
  });

  // Загрузка данных профиля
  useEffect(() => {
    const fetchData = async () => {
      const data = await getUserData(user.id);
      setFormData(data);
    };
    fetchData();
  }, [user.id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await updateUserData(user.id, formData);
      setIsEditing(false);
      alert('Данные сохранены!');
    } catch (error) {
      alert('Ошибка: ' + error.message);
    }
  };

  return (
    <div className="profile">
      <h1>Мой профиль</h1>

      <div className="profile-field">
        <label>ФИО *</label>
        {isEditing ? (
          <input
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
          />
        ) : (
          <span>{formData.fullName}</span>
        )}
      </div>

      <div className="profile-field">
        <label>Дата рождения *</label>
        {isEditing ? (
          <input
            type="date"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
          />
        ) : (
          <span>{formData.dateOfBirth}</span>
        )}
      </div>

      <div className="profile-field">
        <label>Контактный номер *</label>
        {isEditing ? (
          <input
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
          />
        ) : (
          <span>{formData.phoneNumber}</span>
        )}
      </div>

      <div className="profile-field">
        <label>E-mail</label>
        <span>{formData.email}</span> {/* Email обычно не редактируется */}
      </div>

      <div className="profile-field">
        <label>Пол</label>
        {isEditing ? (
          <select
            name="gender"
            value={formData.gender}
            onChange={handleChange}
          >
            <option value="male">Мужской</option>
            <option value="female">Женский</option>
          </select>
        ) : (
          <span>{formData.gender === 'male' ? 'Мужской' : 'Женский'}</span>
        )}
      </div>

      <div className="profile-actions">
        {isEditing ? (
          <button onClick={handleSave}>Сохранить данные</button>
        ) : (
          <button onClick={() => setIsEditing(true)}>Изменить</button>
        )}
      </div>
    </div>
  );
}
