import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductCard.css';
import CustomButton from "../CustomButton/CustomButton";

const ProductCard = ({
  id,
  img,
  model,
  oldPrice,
  newPrice,
  discount,
  availability
}) => {
  const [isFavorite, setIsFavorite] = useState(false);

  const calculateDiscount = () => {
    if (!oldPrice || oldPrice <= newPrice) return null;
    return Math.round(((oldPrice - newPrice) / oldPrice) * 100);
  };

  const discountValue = calculateDiscount() || discount;
  const availabilityClass = typeof availability === 'string' && availability
    ? availability.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '')
    : 'unknown';

  const handleAddToCart = (e) => {
    e.stopPropagation();
    // Заглушка для добавления в корзину
    console.log(`Товар ${id} добавлен в корзину: ${model}`);
    // Здесь можно добавить реальную логику добавления в корзину
  };

  return (
    <Link to={`/product/${id}`} className="product-link">
      <div className="product-card">
        <div className="product-image-wrapper">
          {discountValue && (
            <div className="discount-badge">
              -{discountValue}%
            </div>
          )}
          <img src={img} alt={model} className="product-image" />
        </div>

        <div className="product-content">
          <h3 className="product-title">{model}</h3>

          <div className="product-attributes">
            <span className={`availability ${availabilityClass}`}>
              {availability || 'Не указано'}
            </span>
            <button
              className={`favorite-button ${isFavorite ? 'active' : ''}`}
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                setIsFavorite(!isFavorite);
              }}
            >
              <svg width="24" height="24" viewBox="0 0 24 24">
                <path
                  d="M17.612 2.826C17.172 2.405 16.65 2.072 16.075 1.844C15.5 1.617 14.884 1.5 14.262 1.5C13.64 1.5 13.024 1.617 12.449 1.844C11.874 2.072 11.352 2.405 10.912 2.826L10 3.698L9.087 2.826C8.198 1.977 6.994 1.5 5.737 1.5C4.48 1.5 3.276 1.977 2.388 2.826C1.499 3.675 1 4.826 1 6.027C1 7.227 1.499 8.378 2.388 9.227L3.3 10.099L10 16.5L16.699 10.099L17.612 9.227C18.052 8.807 18.401 8.308 18.639 7.759C18.877 7.21 19 6.621 19 6.027C19 5.432 18.877 4.843 18.639 4.294C18.401 3.745 18.052 3.246 17.612 2.826Z"
                  fill={isFavorite ? "#FF4444" : "none"}
                  stroke={isFavorite ? "#FF4444" : "#0C0C0C"}
                  strokeWidth="2"
                />
              </svg>
            </button>
          </div>

          <div className="price-wrapper">
            {oldPrice && oldPrice > newPrice && (
              <div className="old-price">
                {oldPrice.toLocaleString()} BYN
              </div>
            )}
            <div className="new-price">
              {newPrice.toLocaleString()} BYN
            </div>
          </div>

          <CustomButton
            className="cart-button"
            onClick={handleAddToCart}
          >
            <svg className="cart-icon" viewBox="0 0 24 24">
              <path d="M6 2L3 6v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2V6l-3-4H6zm15 4l-3-4H6L3 6v12h18V6z"/>
              <path d="M16 10a4 4 0 1 1-8 0"/>
            </svg>
            В корзину
          </CustomButton>
        </div>
      </div>
    </Link>
  );
};

export default ProductCard;
