// ProductPage.jsx
import React from 'react';
import { useParams } from 'react-router-dom';
import CustomButton from '../../Components/CustomButton/CustomButton';
// УДАЛЕНО: import './ProductPage.css';
import './ProductPageStyles.css';

const products = [
  {
    id: 1,
    image: "/img/tovar/iphone13.jpg",
    model: "iPhone 14 Pro",
    memory: "256GB",
    newPrice: 4200,
    oldPrice: 4500,
    availability: "В наличии",
    specs: {
      screen: "6.1'' Super Retina XDR",
      cores: "6-ядерный A16 Bionic",
      power: "20W",
      ram: "6GB",
      storage: "256GB",
      camera: "48MP + 12MP + 12MP"
    }
  },
  {
    id: 2,
    image: "/img/tovar/iphon12.jpg",
    model: "Galaxy S23 Ultra",
    memory: "512GB",
    newPrice: 3800,
    oldPrice: 4100,
    availability: "В наличии",
    specs: {
      screen: "6.8'' Dynamic AMOLED 2X",
      cores: "8-ядерный Snapdragon 8 Gen 2",
      power: "25W",
      ram: "12GB",
      storage: "512GB",
      camera: "200MP + 10MP + 12MP"
    }
  },
  {
    id: 6,
    image: "/img/tovar/ipad.jpg",
    model: "iPad Pro 11",
    memory: "256GB",
    newPrice: 1000,
    oldPrice: 1200,
    availability: "В наличии",
    specs: {
      screen: "11'' Liquid Retina",
      cores: "8-ядерный M2",
      power: "20W",
      ram: "8GB",
      storage: "256GB",
      camera: "12MP + 10MP"
    }
  },
  {
    id: 10,
    image: "/img/tovar/oled_tv.jpg",
    model: "OLED55C3",
    memory: "N/A",
    newPrice: 1800,
    oldPrice: 2000,
    availability: "В наличии",
    specs: {
      screen: "55'' OLED",
      cores: "N/A",
      power: "120W",
      ram: "N/A",
      storage: "N/A",
      camera: "N/A"
    }
  }
];

const ProductPage = () => {
  const { productId } = useParams();
  const product = products.find(p => p.id === Number(productId));

  if (!product) {
    return <div className="error">Товар не найден. Проверьте ID: {productId}</div>;
  }

  const handleAddToCart = () => {
    console.log(`Товар ${product.id} добавлен в корзину: ${product.model}`);
  };

  return (
    <div className="product-page-container">
      <div className="product-main-content">
        <div className="product-left-column">
          <div className="product-image-wrapper">
            <img
              src={process.env.PUBLIC_URL + product.image}
              alt={product.model}
              className="product-main-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/400x400?text=No+Image';
              }}
            />
            {product.oldPrice > product.newPrice && (
              <div className="discount-badge">
                -{Math.round(((product.oldPrice - product.newPrice) / product.oldPrice) * 100)}%
              </div>
            )}
            {product.availability === "В наличии" && (
              <div className="availability-badge in-stock">В наличии</div>
            )}
          </div>
        </div>

        <div className="product-center-column">
          <div className="product-details">
            <div className="product-info">
              <h1 className="product-title">{product.model} {product.memory}</h1>
              <div className="price-wrapper">
                {product.oldPrice > product.newPrice && (
                  <div className="old-price">{product.oldPrice.toLocaleString()} BYN</div>
                )}
                <div className="new-price">{product.newPrice.toLocaleString()} BYN</div>
              </div>

              <div className="product-specs">
                <h3>Характеристики</h3>
                <div className="specs-grid">
                  {Object.entries(product.specs).map(([key, value]) =>
                    value !== "N/A" && (
                      <div className="spec-item" key={key}>
                        <span className="spec-title">
                          {key === 'screen' && 'Экран'}
                          {key === 'cores' && 'Процессор'}
                          {key === 'power' && 'Блок питания'}
                          {key === 'ram' && 'Оперативная память'}
                          {key === 'storage' && 'Встроенная память'}
                          {key === 'camera' && 'Камера'}
                        </span>
                        <span className="spec-value">{value}</span>
                      </div>
                    )
                  )}
                </div>
              </div>

              <div className="product-reviews">
                <h3>Отзывы</h3>
                <p>Отзывы пока не добавлены (заглушка)</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="price-floating-container">
        <div className="price-section">
          <div className="product-price">{product.newPrice.toLocaleString()} BYN</div>
          <CustomButton className="add-to-cart-btn" onClick={handleAddToCart}>
            Добавить в корзину
          </CustomButton>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
