import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phone: '',
    birthDate: '',
    isAdmin: false,
  });
  const [orders, setOrders] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return navigate("/login");

    const fetchProfile = async () => {
      try {
        const [userRes, ordersRes, favRes] = await Promise.all([
          fetch(`http://localhost:5085/api/auth/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/orders/user/${storedUser.userId}`),
          fetch(`http://localhost:5085/api/favorite/${storedUser.userId}`),
        ]);

        if (!userRes.ok || !ordersRes.ok || !favRes.ok) throw new Error("Ошибка загрузки данных");

        const user = await userRes.json();
        setUserData({
          username: user.username || '',
          email: user.email || '',
          password: '',
          firstName: user.firstName || '',
          lastName: user.lastName || '',
          phone: user.phone || '',
          birthDate: user.birthDate?.slice(0, 10) || '',
          isAdmin: user.isAdmin || false,
        });

        const orderData = await ordersRes.json();
        const parsedOrders = await Promise.all(
          orderData.map(async (order) => {
            let pdfUrl = null;
            let contractUrl = `http://localhost:5085/api/orders/contract/${order.orderId}`;

            try {
              const pdfRes = await fetch(`http://localhost:5085/api/orders/pdf/${order.orderId}`);
              const pdfData = await pdfRes.json();
              pdfUrl = "http://localhost:5085" + pdfData.pdfUrl;
            } catch {}

            return {
              id: order.orderId,
              date: order.createdAt,
              pdfUrl,
              contractUrl,
              totalPrice: order.items?.reduce((sum, item) => sum + item.price * item.quantity, 0) || 0,
              items: order.items?.map(item => ({
                id: item.orderItemId,
                productId: item.product?.productId || item.productId,
                name: item.product?.model || item.product?.name || 'Без названия',
                image: item.product?.imageUrl || '/img/placeholder.png',
                price: item.price || 0,
                quantity: item.quantity || 1,
              })) || [],
            };
          })
        );

        const favoriteData = await favRes.json();
        setOrders(parsedOrders);
        setFavorites(favoriteData);
        setLoading(false);
      } catch (err) {
        alert("Ошибка загрузки профиля");
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const handleChange = e => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/auth/update`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: storedUser.userId, ...userData }),
      });

      if (res.ok) {
        const updated = await res.json();
        localStorage.setItem('user', JSON.stringify(updated));
        alert('Профиль обновлён');
      } else {
        alert('Ошибка при обновлении профиля');
      }
    } catch {
      alert('Ошибка при сохранении');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleRemoveFavorite = async (productId) => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (!storedUser?.userId) return;

    try {
      const res = await fetch(`http://localhost:5085/api/favorite/${storedUser.userId}/${productId}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        setFavorites(prev => prev.filter(fav => fav.productId !== productId));
        alert('Удалено из избранного');
      } else {
        alert('Ошибка при удалении');
      }
    } catch {
      alert('Ошибка при удалении');
    }
  };

  if (loading) return <div style={{ textAlign: 'center', fontSize: '18px' }}>Загрузка...</div>;

  return (
    <div className="profile-wrapper">
      <h2 className="profile-title">{userData.isAdmin ? 'Панель администратора' : 'Личный кабинет'}</h2>

      {userData.isAdmin && (
        <div className="admin-section">
          <h3>Управление</h3>
          <button onClick={() => navigate('/admin/users')}>Пользователи</button>
          <button onClick={() => navigate('/admin/orders')}>Заказы</button>
          <button onClick={() => navigate('/admin/products')}>Товары</button>
        </div>
      )}

      <div className="profile-form">
        <input type="text" name="username" placeholder="Имя пользователя" value={userData.username} onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" value={userData.email} onChange={handleChange} />
        <input type="password" name="password" placeholder="Пароль" value={userData.password} onChange={handleChange} />
        <input type="text" name="firstName" placeholder="Имя" value={userData.firstName} onChange={handleChange} />
        <input type="text" name="lastName" placeholder="Фамилия" value={userData.lastName} onChange={handleChange} />
        <input type="tel" name="phone" placeholder="Телефон" value={userData.phone} onChange={handleChange} />
        <input type="date" name="birthDate" value={userData.birthDate} onChange={handleChange} />

        <div className="profile-actions">
          <button onClick={handleSave}>Сохранить</button>
          <button onClick={handleLogout}>Выйти</button>
        </div>
      </div>

      {!userData.isAdmin && (
        <>
          <div className="profile-section-title">Мои заказы</div>
          {orders.length === 0 ? (
            <p style={{ textAlign: 'center' }}>У вас нет заказов.</p>
          ) : (
            <div className="orders-wrapper">
              {orders.map(order => (
                <div key={order.id} className="order-card">
                  <div className="order-header">
                    <div>
                      <h4>Заказ №{order.id}</h4>
                      <div>{new Date(order.date).toLocaleDateString()}</div>
                    </div>
                    <div className="order-sum">{order.totalPrice.toFixed(2)} BYN</div>
                  </div>
                  <div className="order-items">
                    {order.items.map(item => (
                      <div key={item.id} className="order-item" onClick={() => navigate(`/products/${item.productId}`)}>
                        <img src={item.image} alt={item.name} />
                        <div className="item-info">
                          <div>{item.name}</div>
                          <div>× {item.quantity}</div>
                        </div>
                        <div className="item-price">{(item.price * item.quantity).toFixed(2)} BYN</div>
                      </div>
                    ))}
                  </div>
                  <div className="order-links">
                    {order.pdfUrl && (
                      <a href={order.pdfUrl} target="_blank" rel="noreferrer">📄 Гарантия</a>
                    )}
                    <a href={order.contractUrl} target="_blank" rel="noreferrer">📃 Договор</a>
                  </div>
                </div>
              ))}
            </div>
          )}

          <div className="profile-section-title">Избранное</div>
          {favorites.length === 0 ? (
            <p style={{ textAlign: 'center' }}>Нет избранных товаров.</p>
          ) : (
            <div className="cart-grid">
              {favorites.map(fav => {
                const product = fav.product;
                if (!product) return null;
                return (
                  <div key={fav.productId} className="cart-item">
                    <img src={product.imageUrl || '/img/placeholder.png'} alt={product.model || 'Товар'} />
                    <div>{product.model || product.name}</div>
                    <div>{product.newPrice || 0} BYN</div>
                    <button onClick={() => handleRemoveFavorite(fav.productId)}>Удалить</button>
                  </div>
                );
              })}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ProfilePage;
