// Cart.jsx — React компонент с фото, стилями и безопасной загрузкой
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Checkout.css';

const Cart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser?.userId) return navigate("/login");

    fetch(`http://localhost:5085/api/cart/${storedUser.userId}`)
      .then(res => res.json())
      .then(data => {
        const items = Array.isArray(data.items) ? data.items : data;
        const parsed = items.map(item => {
          const product = item.product || {};
          return {
            ...item,
            productId: product.productId || item.productId,
            name: product.model || product.name || "Товар",
            image: (product.images && product.images[0]?.imageUrl) || "/img/placeholder.png",
            price: product.newPrice ?? product.price ?? 0,
          };
        });
        setCartItems(parsed);
      })
      .catch(err => {
        console.error("Ошибка загрузки корзины:", err);
        setCartItems([]);
      });
  }, [navigate]);

  const goToCheckout = () => {
    navigate("/checkout");
  };

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="checkout-wrapper">
      <h2>Корзина</h2>
      {cartItems.length === 0 ? (
        <p>Корзина пуста.</p>
      ) : (
        <>
          <div className="cart-grid">
            {cartItems.map(item => (
              <div key={item.cartItemId} className="cart-item">
                <img src={item.image} alt={item.name} className="cart-image" />
                <h3 className="cart-title">{item.name}</h3>
                <div className="new-price">{item.price} BYN</div>
                <div className="quantity">Количество: {item.quantity}</div>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <strong>Итого: {total.toFixed(2)} BYN</strong>
            <button onClick={goToCheckout} className="checkout-btn">Перейти к оформлению</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
